INSERT INTO `indexArticle` (`id`, `hotel_category`, `image`, `country`, `city`, `hotel`, `content`, `price`, `latitude`, `longitude`) VALUES
(1, 3, 'tunisie.jpg', 'TUNISIE', '', 'HÔTEL PARADIS PALACE 4', 'La Tunisie, destination appréciée des Français, se démarque par son paysage ensoleillé et accueillant ponctué de plages de sable...', 'Dès 228 €', '36.3908012', '10.55902'),
(2, 3, 'allemagne-berlin.jpg', 'ALLEMAGNE', '- Berlin', 'CALIFORNIA AM KURFÜRSTENDAMM 4', '- A quelques pas des commerces <br>\r\n- Une terrasse en été <br>\r\n- Un personnel accueillant...<br>', 'Dès 604 €', '52.502676', '13.323901'),
(3, 3, 'Mexique-cancun.jpg', 'MEXIQUE', '- Cancùn', 'CLUB CORALIA BLUEBAY GRAND ESMERALDA 5', '- Réservez tôt : jusqu\'à -300euros* <br>\r\n- Un hôtel haut de gamme <br>\r\n- Un agréable centre de bien-être ... ', 'Dès 934 €', '20.700257', '-87.012947'),
(4, 3, 'Republique dominicaine.jpg', 'RÉPUBLIQUE DOMINICAINE', '', 'GRAND BAHIA PRINCIPE SAN JUAN 5', '- Une immense plage privée  <br>\r\n- Un magnifique jardin tropical  <br>\r\n- Des chambres spacieuses...', 'Dès 1 182 €', '18.479024', '-69.897991'),
(5, 3, 'maurice.jpg', 'MAURICE', '', 'THE BAY HOTEL 3 SUP - HÔTEL DE CHARME', '- Une petite structure  <br>\r\n- Un cadre intimiste  <br>\r\n- Une plage de sable fin...', 'Dès 1 645 €', '-20.279401', '57.374494'),
(6, 3, 'malte.jpg', 'MALTE', '', 'DOLMEN RESORT HÔTEL 4', 'L’un des plus grands complexes de l’île. Situé en bord de mer et à proximité du centre-ville. Ne manquez pas le casino de l’hôtel...', 'Dès 499 €', '35.954334', '14.418418'),
(7, 3, 'indonésie Bali.jpg', 'INDONÉSIE ', ' - Bali', 'UNITED COLORS OF BALI 3', '- Un endroit privilégié  <br>\r\n- Un hébergement typiquement balinais  <br>\r\n- Au coeur des rizières<br>\r\n- Hébergement original', 'Dès 1 072 €', '-8.6478175', '115.13851920000002'),
(8, 3, 'kenya.jpg', 'KENYA', '', 'HÔTEL NEPTUNE VILLAGE BEACH RESORT & SPA 4', 'Le Kenya, terre des Masaïs et de la savane à perte de vue, offre d\'innombrables opportunités pour la...', 'Dès 1 229 €', '-4.2975511', '39.58216779999998'),
(9, 3, 'turquie.jpg', 'TURQUIE', ' - Bodrum', 'SALMAKIS RESORT & SPA - 4', ' - directement sur la plage, à flanc de coteau  <br>\r\n - détails de plage: plage privée de l\'hôtel; sable/galets;', 'Dès 604 €', '37.039342', '27.430355700000064'),
(10, 1, 'amsterdam.jpg', 'PAYS-BAS', ' - Amsterdam', 'BILDERBERG GARDEN HOTEL 5', 'Decoré avec classe et goût et situé au calme. À une distance à pied de la Museumplein d\'environ 10 min. Et le tram à  ...', 'Dès 188 €', '52.3511447', '4.874076400000035'),
(11, 1, 'tenerife .jpg', 'TENERIFE', ' - Playa de la Arena', 'BAHIA FLAMINGO 3', 'Cette hôtel face à la mer et offrant un merveilleux paysage sur l\'île de la Gomera... ', 'Dès 384 €', '28.226703', '-16.838712999999984'),
(12, 1, 'punta cana.jpg', 'PUNTA CANA', '', 'CARIBE CLUB PRINCESS BEACH RESORT & SPA 4', '- Une plage paradisiaque <br>\r\n- Une restauration variées <br>\r\n- De nombreuses activités <br>', 'Dès 846 €', '18.7027638', '-68.44193150000001'),
(13, 1, 'suede.jpg', 'SUEDE', ' - Stockholm', 'ELITE PALACE HOTEL ****', 'L’hôtel Elite Palace est un établissement quatre étoiles bénéficiant d’un excellent emplacement au calme. Dans le célèbre quartier de Vasastaden.', 'Dès 124 €', '59.34675', '18.03932850000001'),
(14, 1, 'valais.jpg', 'SUISSE', '- Valais', 'HÔTEL CHALET ROYAL ***', 'Hôtel 3 étoiles très soigné et accueillant, dans le style rustique des chalets. Un parking se trouve en dessous de l’hôtel. L’hôtel Chalet...', 'Dès 54 €', '46.1958298', '7.342751399999997'),
(15, 1, 'séville.jpg', 'ESPAGNE', '- Séville', 'CLUB CORALIA OHTELS ISLANTILLA 4', '- Réservez tôt : jusqu\'à -300euros* <br>\r\n- A proximité de la plage <br>\r\n- Un sublime centre de bien-être <br>', 'Dès 525 €', '37.2085133', '-7.2285967000000255'),
(16, 1, 'santorin.jpg', 'GRÈCE ET SES ÎLES', '- Santorin', 'EL GRECO ****', 'Bel hôtel, idéalement situé près de Fira qui vous séduira par son architecture traditionnelle et colorée...', 'Dès 699 €', '36.411172', '25.43478700000003'),
(17, 1, 'Prague.jpg', 'REPUBLIQUE-TCHÈQUE', '- Prague', 'HOTEL PRAHA ****', 'À l\'Hotel Praha, vous pouvez faire usage gratuitement du parking et du Wifi. Cet hôtel...', 'Dès 59 €', '50.0718899', '14.44626160000007'),
(18, 1, 'londres.jpg', 'ROYAUME-UNI', '- Londres', 'THE QUEEN\'S PARK ***', 'The Queens Park à Londres est un hôtel trois étoiles soigné avec des chambres confortables. L\'hôtel se situe à env. 5 min...', 'Dès 69 €', '51.5117823', '-0.18503639999994448'),
(19, 2, 'andorre.jpg', 'ANDORRE', '', 'HÔTEL PRINCESA PARC ****', 'Situé à 100 m du centre du village d\'Arinsal, l\'hôtel Princesa Parc et tout proche de départ de sentiers de randonnées...', 'Dès 141 €', '42.574005', '1.482236999999941'),
(20, 2, 'Athènes.jpg', 'GRÈCE ET SES ILES', '- Athènes', 'HÔTEL ALKISTIS HOTEL', 'Situé dans la mer Egée, l\'archipel des Cyclades est composé d\'environ 2200 îles, îlots et îlots-rochers...', 'Dès 453 €', '37.4689188', '25.31962850000002'),
(21, 2, 'cuba.jpg', 'CUBA', '- La Havane', 'ROC PRESIDENTE ****', 'Situé au coeur du quartier du Vedado, à 200 m du Malecon et à 10 mn du centre historique de la Vieille ville de La Havane, cet hôtel des années 20...', 'Dès 993 €', '23.1135925', '-82.36659559999998'),
(22, 2, 'florence.jpg', 'ITALIE', '- Florence', 'CHIANTI VILLAGE **** ', '-Appartements modernes <br>\r\n-Bonne cuisine <br>\r\n-De nombreuses activités <br>\r\n-Piscine en plein air ', 'Dès 74 €', '43.55813', '11.207953999999972'),
(23, 2, 'guadeloupe.jpg', 'ANTILLES', '- Guadeloupe', 'HÔTEL MARIFA', 'Bien caché dans un quartier résidentiel, le Marifa est un petit hôtel de charme avec piscine proposant des studios et des duplex... ', 'Dès 702 €', '16.2133079', '-61.43203889999995'),
(24, 2, 'Dubai.jpg', 'EMIRATS ARABES UNIS', '- Dubaï', 'CITYMAX BUR DUBAÏ 3', '- Un emplacement idéal en centre-ville <br>\r\n- Un toit-terrasse avec piscine <br>\r\n- Un personnel à l\'écoute', 'Dès 695 €', '25.2514369', '55.29000180000003'),
(25, 2, 'Danemark.jpg', 'DANEMARK', '- Sjaelland', 'COMFORT HÔTEL VESTERBRO ***', 'Comfort Hotel Vesterbro est un hôtel quatre étoiles agréable situé dans le quartier à la mode de Vesterbro...', 'Dès 150 €', '55.67286069999999', '12.558972100000005'),
(26, 2, 'amsterdams.jpg', 'PAYS-BAS', '- Amsterdam', 'BEST WESTERN PREMIER HÔTEL COUTURE ****', 'L’hôtel 4 étoiles Best Western Couture est accessible aussi bien en transport en commun qu’en...', 'Dès 179 €', '52.3511137', '4.841162899999972'),
(27, 2, 'kenya2.jpg', 'KENYA', '', 'AMANI TIWI BEACH RESORT ****', ' - Hôtel au bord de mer <br>\r\n - Détails de plage: Tiwi Beach; sable fin, très longue plage <br>\r\n - De nombreuses activités\r\n', 'Dès 837 €', '-4.1816115', '39.46056120000003');
INSERT INTO `trip` (`idTrip`, `img`, `home_img`, `desk_img`, `resto_img`, `room_img`, `essential`, `detail`, `price_have`, `price_dont`, `priceRoom`, `priceRoomSup`) VALUES
(1, 'tunisie_pool.jpg', 'tunisie_recep.jpg', 'tunisie_desk.jpg', 'tunisie_resto.jpg', 'tunisie_room.jpg', '<h2> LA DESTINATION </h2>  \r\nLa Crète est la plus vaste des îles grecques et la plus au sud de la mer Egée. Des kilomètres de plage, des montagnes abruptes, de fertiles plaines... ainsi se compose un paysage sans cesse changeant, tantôt boisé, verdoyant et paisible. Vous découvrirez de nombreuses villes ports, pleines de charme tout au long de la côte ...', '<h2>Situation </h2> <br/>\r\n\r\n<u><b>LA DESTINATION</b></u> <br/><br/>\r\n\r\nLa Crète est la plus vaste des îles grecques et la plus au sud de la mer Egée. Des kilomètres de plage, des montagnes abruptes, de fertiles plaines... ainsi se compose un paysage sans cesse changeant, tantôt boisé, verdoyant et paisible. Vous découvrirez de nombreuses villes ports, pleines de charme tout au long de la côte mais aussi de grands sites archéologiques qui jouèrent un rôle majeur dans l\'histoire de l\'île, des temps préhistoriques à nos jours. <br/><br/>\r\n\r\nSitué sur la côte nord de l\'île, à 3,5 km d\'Agia Pelagia, 17 km de la ville d\' Héraklion et 20 km de son aéroport. La ville de Réthymnon est à 50 km à l\'ouest. \r\nVous trouverez une plage publique à environ 1500 m de l\'Angel Village. <br/><br/>\r\n\r\n<u><b>INFO VERITE :</b></u> <br/><br/>\r\n\r\nNiché en haut d\'une colline, l\'hôtel Angel Village 3* dispose d\'une vue panoramique sur la baie d\'Agia Pelagia mais est isolé des zones touristiques avec peu de commerces dans son environnement. Idéal pour les vacanciers recherchant le calme et la quiétude, il constitue un bon point de chute pour découvrir l\'île. Pour ce faire il est fortement conseillé de louer une voiture. <br/>\r\nL\'enceinte de l\'hôtel comporte de nombreux escaliers : il n\'est pas recommandé aux personnes à mobilité réduite ou aux familles avec enfants en bas-âge. <br/><br/>\r\n\r\n<u><b>A NOTER :</b></u> <br/><br/>\r\n\r\nCertains vols depuis et à destination de la Crète peuvent avoir lieu à des horaires tardifs (vols de nuit). La première et/ou la dernière nuit du séjour peuvent donc être consacrées au transport. <br/>\r\nA l\'inverse, le décollage de votre vol retour pourra avoir lieu dans la nuit suivant le dernier jour de votre séjour (arrivée le lendemain matin de la date de retour indiquée sur votre bon de commande).<br/><br/>\r\n\r\n<h2>Logement</h2>\r\n\r\nLes chambres de l\'Angel Village 3* sont réparties dans 3 bâtiments d\'un étage, en plus du bâtiment principal où se trouve la réception. L\'hôtel est composé de 33 chambres, et de 10 appartements pouvant accueillir 4 personnes. <br/>\r\nDurant votre séjour, vous serez logés en chambre double standard. <br/>\r\n\r\nToutes les chambres, ainsi que les appartements, sont équipés d\'un réfrigérateur, téléphone, radio-réveil, salle de douche avec sèche-cheveux, coffre-fort (avec supplément), et climatisation (supplément de 5 euros par jour, sauf du 27/06 au 04/09*). Toutes ont des balcons ou terrasses. A noter les chambres ne disposent pas de télévision. <br/>\r\n\r\nL\'hôtel ne propose pas de chambres adaptées aux personnes handicapées. <br/><br/>\r\n\r\n<u><b>INFO VERITE :</b></u> <br/><br/>\r\nLes chambres sont de confort simple. <br/>\r\n*Dates données à titre indicatif, sous réserve de modification sans préavis par l\'hôtelier. <br/><br/>\r\n\r\n<u><b>A NOTER :</b></u> <br/><br/>\r\n\r\nSelon la réglementation internationale hôtelière, les chambres doivent impérativement être libérées avant 12h00 le jour du départ. Au delà de cette heure, l\'hôtelier est en droit de facturer le client.<br/><br/>\r\n\r\n<h2>Conditions de pension</h2>\r\nLa disponibilité des différents modes de pension n\'est pas garantie . La disponibilité de la pension choisie sera confirmée au moment de la réservation<br/><br/>\r\n\r\n<h2>Demi Pension</h2>\r\nDurant votre séjour, vous bénéficiez de la formule demi pension (hors boissons y compris l\'eau). <br/><br/>\r\n\r\nVotre formule comprend les petits-déjeuners et dîners servis au restaurant principal sous forme de buffets. <br/><br/>\r\n\r\nL\'hôtel également dispose également d\'un snack-bar (boissons et plats en supplément).<br/> <br/>\r\n\r\n<u><b>A noter:</b></u> <br/><br/>\r\n\r\n- Les horaires du restaurant et snack bar sont à consulter sur place au moment de votre séjour <br/>\r\n- Les boissons ne sont pas incluses (y compris l\'eau en carafe et/ou en bouteille).<br/> \r\n- Tenue correcte exigée au restaurant : pantalon long pour les messieurs.<br/><br/>\r\n\r\n<h2>Equipements</h2>\r\n\r\nMini-market. Arrêt de bus locaux (payants) à 150 m de l\'hôtel.<br/>\r\nAccès Internet WIFI dans les espaces communs (avec supplément).<br/><br/>\r\n\r\n<h2>Sports et loisirs</h2>\r\n\r\nL\'hôtel Angel Village 3* dispose de : piscine, tennis de table, billard (payant), 1 court de tennis (prêt de raquettes et balles), 1 terrain de basket, terrain de pétanque (boules à disposition). A environ 1500 m de l\'hôtel en contrebas, vous trouverez une plage publique avec parasols et chaises longues (avec supplément). Une navette gratuite vous emmènera depuis l\'hôtel jusqu\'à la plage (Horaires à consulter sur place).<br/><br/>\r\n\r\n<h2>Sports et loisirs payants</h2>\r\nEn supplément vous pourrez disposer du billard.<br/><br/>\r\n\r\n<h2>Animation</h2>\r\nCet hôtel ne propose pas d\'animation.<br/><br/>\r\n\r\n<h2>Enfants</h2>\r\nL\'hôtel met à disposition des enfants un bassin et une aire de jeux.<br/><br/>\r\n\r\n<h2>Excursions</h2>\r\n\r\nLa Crète - L\'île de toutes les légendes <br/>\r\nCette île, dont les habitants ont toutes les raisons d\'être fiers, possède une diversité de paysages aussi paradisiaques les uns que les autres : soleil, plages, mer, et montagne, composent ce pays de contrastes. Les Crétois sont également fiers de leurs nombreuses légendes et de leur gastronomie réputée. <br/><br/>\r\n\r\nCertaines excursions peuvent s\'effectuer dans les régions montagneuses de la Crète, il est alors astucieux de prendre dans ses bagages une paire de chaussures de marche ou de basket. <br/><br/>\r\n\r\nExcursions facultatives, données à titre indicatif, à réserver et à régler sur place :<br/> <br/>\r\n\r\n<b>ILE DE SPINALONGA, L\'ILE DES OUBLIES</b> <br/>\r\nRoute en car jusqu\'à Elounda, agréable petit port de pêche, puis traversée en bateau jusqu\'à Spinalonga, l\'île des lépreux, pour une visite aux travers des ruelles et remparts de pierres offrant aux amateurs la possibilité de réaliser de nombreux clichés. Vous aurez l\'occasion de vous baigner dans les eaux limpides du golfe avant de déjeuner en taverne. L\'après-midi visite d\'Agios Nikolaos, station balnéaire connue pour son lac et temps libre. <br/><br/>\r\n\r\n<b>CHRISSI ISLAND, L\'ILE DOREE</b> <br/>\r\nL\'île de Chrissi est située au sud de Iérapétra. Autrefois habitée par les byzantins, l\'île est aujourd\'hui le sanctuaire de la faune et de la flore marine. C\'est un îlot connu pour son incroyable beauté naturelle, ses eaux exotiques dans toutes les nuances de bleu et de vert, ses plages dorées, d\'où elle tire son nom, sa forêt aux cèdres bicentenaires et ses innombrables coquillages. Chrissi est un morceau de paradis sur Terre, le parc naturel le plus méridional d\'Europe et un joyau de la Méditerranée. <br/><br/>\r\n\r\n<b>JEEP SAFARI </b> <br/>\r\nEn route pour un peu d\'aventure avec cette journée au volant d\'un 4x4 ! En empruntant routes, pistes et chemins traversant de pittoresques villages, vous pourrez encore apercevoir quelques villageois crétois toujours vêtus du costume traditionnel. Ambiance assurée et souvenirs inoubliables. <br/><br/>\r\n\r\n<b>KNOSSOS-ARCHANES-MUSEE-HERAKLION</b> <br/>\r\nDépart en début de matinée vers Héraklion. Visite du site archéologique du palais de Knossos qui vous plongera dans le monde antique (1900 avant J-C) et évoquera les légendes du roi Minos, Thésée et le fil d\'Ariane ainsi que du Minotaure. Route vers Héraklion pour une promenade dans la ville : découverte de la cathédrale d\'Agios Minas, la fontaine Morosini, sans oublier le marché de la rue 1866 ; temps libre pour déjeuner avant la visite du musée archéologique. <br/><br/>\r\n\r\n<b>RANDONNEE DANS LES GORGES DE SAMARIA</b> <br/>\r\nPartant du plateau d\'Omalos le long d\'un parcours ombragé bordé de cyprès, pins et autres arbres aux formes surprenantes, vous atteindrez les fameuses \"Portes de fer\" hautes de près de 500 m, se resserrant sur un étroit passage de 3 m de large. A votre rythme, parcourez les 16 km de gorges, certainement parmi les plus longues d\'Europe, sur un dénivelé de 1227 m. Nous recommandons vivement de partir avec de bonnes chaussures et un maillot de bain. <br/><br/>\r\n\r\n<b>GRAMVOUSSA, LES CARAIBES CRETOISES</b> <br/>\r\nCap est mis sur l\'ouest de la Crète, en partance du port de Kissamos pour découvrir le lagon de Balos et l\'île de Gramvoussa. Deux merveilles à découvrir absolument afin de profiter des eaux turquoises du lagon, vous donnant l\'impression de flirter avec le paradis. L\'île de Gramvoussa abrite une forteresse vénitienne que vous atteindrez en 20 min de marche le long de la colline. <br/><br/>\r\n\r\n', '- Les vols internationaux au départ de France/Analipsis A/R, <br/>\r\n- L\'accueil et l\'assistance, <br/>\r\n- Les transferts entre l\'aéroport et l\'hôtel sous réserve de disponibilité au moment de la réservation (information donnée sur le détail de la prestation, au cours de la réservation), <br/>\r\n- Le logement en chambre double standard selon la durée choisie, <br/>\r\n- La pension validée au moment de la réservation (selon sa disponibilité) : le type de pension sera repris sur vos documents de voyage, <br/>\r\n- Les taxes d\'aéroport (sujettes à évolution) et la taxe de solidarité (incluant la hausse carburant obligatoire sous réserve de hausse supplémentaire).', '- Les frais de dossier <br/>\r\n- L\'excédent bagages des sacs hors gabarit (à régler lors de l\'enregistrement à l\'aéroport), <br/>\r\n- Les assurances facultatives, <br/>\r\n- La taxe de sortie du territoire et/ou la carte touristique si existante,<br/> \r\n- Les activités payantes, <br/>\r\n- Les dépenses d\'ordre personnel, <br/>\r\n- Le visa et les frais de services obligatoires si existants, <br/>\r\n- Les pourboires usuels.<br/>', '456', '598'),
(2, 'berlin_bar.jpg', 'berlin_home.jpg', 'berlin_recep.jpg', 'berlin_resto2.jpg', 'berlin_room.jpg', '- A quelques pas des commerces <br/>\r\n- Une terrasse en été<br/>\r\n- Un personnel accueillant<br/>', '<h2>Situation</h2>\r\n\r\nLa capitale allemande a réussi à s’imposer comme la référence en matière de mode, d’art, de design et de musique dans le monde. En vous baladant dans les rues de Berlin, vous allez être transporté dans le passé qui est omniprésent dans la ville.<br/>\r\nL’hôtel Berlin Mark 4* occupe un élégant bâtiment résidentiel et est situé dans le quartier à environ 5 min à pied de la rue commerçante de « Kurfürstendamm ». Vous pourrez également découvrir à moins de 15min à pied, l’église du Souvenir de l’Empereur Guillaume, et le Zoo de Berlin.<br/>\r\nL’hôtel est à proximoté de la station de metro « Uhlandstrasse » (10min à pied), qui vous permettra de rejoindre facilement le centre-ville et découvrir les sites touristiques de la ville.<br/>\r\nL’aéroport de Berlin Tegel se situe à 20min en voiture et l’aéroport de Berlin Schonefeld se situe à 25min environ de l’hôtel en voiture.<br/><br/>\r\n\r\n<h2>Hébergement</h2>\r\n\r\nL’hôtel Berlin Mark 4* dispose de 239 chambres spacieuses et décorées avec un style moderne. Les chambres sont équipées d’une salle de bain avec sèche-cheveux et articles de toilettes, d’une télévision à écran plat avec chaînes par satellite, d’un téléphone, d’un bureau, de la climatisation et du chauffage à réglage individuel, d’une table à repasser (sur demande), d’un coffre-fort, d’un minibar et de l’accès Wi-Fi.\r\nLit bébé disponible gratuitement sur demande.<br/><br/>\r\n\r\n<h2>Restauration</h2>\r\n\r\nL’hôtel propose le petit-déjeuner servis sous forme de buffet de 6h00 à 10h30*. L’établissement dispose d’un bar/salon mais également d’un snack bar/épicerie.\r\nEn été vous pourrez profiter de la terrasse de l’hôtel.<br/>\r\n*Les horaires sont communiqués à titre indicatif<br/><br/>\r\n\r\n<h2>Equipements et activités</h2>\r\n\r\nL’hôtel propose les services suivants sans frais supplémentaires :<br/>\r\n- Réception ouverte 24h/24<br/>\r\n- Centre d’affaires ouvert 24h/24h<br/>\r\n- Service d’arrivée express<br/>\r\n- Accès Wi-Fi gratuit<br/> \r\n- Télévision dans les espaces communs<br/>\r\n- Journaux gratuits dans le hall<br/>\r\n- Stockage des bagages<br/><br/>\r\n\r\nAvec supplément, vous aurez accès à d’autres services de l’hôtel :<br/>\r\n- Service d’étage<br/>\r\n- Parking sans voiturer (réservation obligatoire)<br/>\r\n- Service de nettoyage à sec-blanchisserie<br/>\r\n- Salle de conférence de 180m2<br/>\r\n- Bureau d’informations touristique<br/><br/>\r\n\r\n<h2>Notre Avis</h2>\r\n\r\nL’hôtel Berlin Mark 4* propose de vous accueillir dans un bâtiment élégant. L’espace de ses chambres et la qualité de service, rendra votre séjour agréable et serein. Grâce à sa proximité avec les transports en commun, vous pourrez découvrir facilement la ville. Cet hôtel est idéal pour les voyages en couple, en famille ou entre amis.<br/>\r\n', '- les vols aller et retour<br/>\r\n- les taxes aéroportuaires et surcharges carburant (soumis à variation)<br/>\r\n- l’hébergement en chambre double pour la durée du séjour<br/>\r\n- la pension selon programme<br/>', '- les transferts<br/>\r\n- les frais de dossier<br/>\r\n- les assurances complémentaires facultatives<br/>\r\n- les prestations payantes (activités, excursions…)<br/>\r\n- les dépenses personnelles<br/>\r\n- les pourboires usuels<br/>\r\n- les éventuelles taxes de sortie du territoire et visa d’entrée<br/>\r\n- les éventuelles taxes de séjours (à régler sur place à l’hôtel) et frais de ménage<br/>\r\n- les repas non mentionnés<br/>\r\n- les éventuels services / resort Fees<br/>', '1208', '1250'),
(3, 'cancun_img.jpg', 'cancun_recep.jpg', 'cancun_home.jpg', 'cancun_resto.jpg', 'cancun_room.jpg', '- Réservez tôt : jusqu\'à -300euros*</br>\r\n- Un hôtel haut de gamme</br>\r\n- Un agréable centre de bien-être', '<h2>Situation</h2>\r\n\r\nLe Club Coralia BlueBay Grand Esmeralda 5* bénéficie d’un cadre privilégié à Playa del Carmen, dans la zone naturelle mexicaine de Riviera Maya.<br/>\r\nCet établissement de 180 hectares est en front de mer, entouré d’une jungle abritant de fabuleux sites archéologiques mayas et de mangroves.<br/>\r\nL’hôtel se trouve à 15 minutes de Playa del Carmen, et à 45 minutes de la ville de Cancun et de l’aéroport international.<br/><br/>\r\n\r\n<h2>Hébergement</h2>\r\n\r\nCet hôtel de charme à l’architecture contemporaine possède 979 chambres élégamment décorées réparties sur plusieurs bâtiments.<br/><br/>\r\n\r\nDurant votre séjour vous serez logés en :<br/><br/>\r\n\r\n- Chambres Deluxe Garden View (environ 35 m2), elles disposent de tout le confort moderne : 1 lit king size ou deux lits doubles, balcon ou d’une terrasse offrant une vue sur les jardins, climatisation, ventilateur, coin bureau, minibar, cafetière, coffre-fort (en supplément), télévision à écran plat, internet (en supplément), téléphone, radio-réveil, nécessaire à repasser, une salle de bains complète avec sèche-cheveux.<br/><br/>\r\n\r\nL’occupation maximale est de : 3 adultes, ou 2 adultes + 2 enfants<br/><br/>\r\n\r\n<h2>Restauration</h2>\r\n\r\nLe Club Coralia BlueBay Grand Esmeralda 5* vous propose un séjour en formule tout inclus, plusieurs restaurants sont à votre disposition :<br/><br/>\r\n\r\n<u>Deux restaurants buffets :</u><br/>\r\n- Le restaurant « Festival » : ouvert pour les petits déjeuners de 07h à 11h*, les déjeuners de 12h à 15h*, et les dîners de 18h à 22h*,<br/>\r\n- Le restaurant « Jaal-Ha » : ouvert pour les petits déjeuners de 07h à 11h*, et les déjeuners de 12h à 15h*.<br/><br/>\r\n\r\n<u>Différents restaurants à la carte sont également accessibles de 18h à 22h* (sur réservation) :</u><br/>\r\n- « Il Cuore de Rossi » sert une cuisine italienne raffinée ;<br/>\r\n- « Don Isi » propose une excellente cuisine mexicaine ;<br/>\r\n- « L’Atelier » présente une cuisine française traditionnelle ;<br/>\r\n- « Sea Food » est spécialisé dans les poissons et les fruits de mer ;<br/>\r\n- « Steak House » propose une variété de viandes américaines ;<br/>\r\n- « Oriental » sert des spécialités orientales.<br/><br/>\r\n\r\nPour les petites faims, un snack bar est disponible le matin 07h à 11h*, le midi de 12h à 15h* et le soir de 23h à 1h*.<br/><br/>\r\n\r\nLes boissons locales alcoolisées et non alcoolisées sont servies à volonté dans les différents bars de l’établissement.<br/><br/>\r\n\r\n*Les horaires sont communiqués à titre indicatif.<br/><br/>\r\n\r\n<h2>Equipements et activités</h2>\r\n\r\n<b><u>L’animation adulte en Club Coralia</b></u><br/><br/>\r\n\r\nEn Club Coralia, nous avons la volonté de vous offrir une expérience club, plus axée sur les plaisirs simples des vacances. Notre équipe d’animateurs francophones vous proposera un programme d’animation en journée et en soirée, où les activités culturelles viendront compléter les « grands classiques » du club, tout en respectant le rythme de chacun.<br/><br/>\r\n\r\n- En journée : en plus des activités sportives et ludiques qui font la richesse du programme d’animation des clubs Coralia nos animateurs vous feront découvrir des activités culturelles typiques : cours de cuisine locale et de cocktail, cours de danse locale, découverte de la langue locale.<br/><br/>\r\n\r\n- En soirée : nous vous proposerons des soirées rythmées et conviviales, encadrées par nos animateurs : soirée festive, sunset cocktail, soirée Casino, soirée white, spectacle folklorique.<br/><br/>\r\n\r\nCertaines activités peuvent-être annulées en fonction des conditions météorologiques. Enfin, sachez qu’au sein de l’hôtel, une équipe d’animation internationale vous proposera un programme d’activités variés, pour compléter le programme d’animation Coralia (sports collectifs, gyms, spectacle,…).<br/><br/>\r\n\r\n<b><u>L’animation enfant en Club Coralia</b></u><br/><br/>\r\n\r\n- Le Coralia Kids Club s’adresse aux enfants de 4 à 12 ans. Les animateurs qualifiés accueilleront vos enfants 6 jours sur 7, de 09h30 à 17h30 (accueil en continu) pour leur proposer un programme d’animation qui leur est spécialement dédié. Une fois par semaine et durant les vacances scolaires, vos enfants seront pris en charge dès le diner pour la soirée Kids Club. Les parents pourront ainsi profiter d’un moment privilégié.<br/><br/>\r\n\r\n- Le Coralia Club Ado s’adresse aux adolescents de 13 à 17 ans uniquement pendant les vacances scolaires avec un programme à la carte, qui regroupera de nombreuses activités sportives, culturelles, et de rencontres.<br/><br/>\r\n\r\n<b></u>Activités de l’hôtel</b></u><br/><br/>\r\n\r\nVous pourrez vous détendre sur la plage aménagée de palapas, ou encore au bord des trois piscines extérieures équipées de chaises longues et de parasols, dont un espace pour les enfants.<br/><br/>\r\n\r\n<u>Pour les plus sportifs plusieurs activités disponibles :</u> Tennis, volleyball, tennis de table, salle de sport, snorkeling, kayaks et planche à voile.<br/><br/>\r\n\r\n<u>Autres activités disponibles (en supplément) :</u> golf, plongée sous-marine, sorties pêche, catamaran.<br/><br/>\r\n\r\nAprès de telles activités, rien de mieux qu’un moment de relaxation au BlueBay Spa de l’hôtel, un espace moderne entièrement consacré au calme et à la sérénité.<br/><br/>\r\n\r\n<u>Autres services (payants) :</u> service de babysitting (sur demande), service médical.<br/><br/>\r\n \r\n<b><u>A noter :</b></u> Selon les conditions météorologiques, les vents et marées peuvent ramener des algues sur les plages.<br/><br/>\r\n\r\n<h2>Excursions</h2>\r\n\r\n<b><u>A savoir :</b></u><br/><br/>\r\n\r\nLes prix et descriptifs des excursions sont donnés à titre indicatif. Toute excursion pourra être modifiée sans préavis et selon certains impératifs indépendants de notre volonté. Les jours de départs seront fixés sur place en fonction de la saison et des conditions météorologiques. Veuillez noter que les excursions sont réservables sur place et les cartes de crédit ne sont pas acceptées.<br/><br/>\r\n\r\n<h2>Notre avis</h2>\r\n\r\nLe Club Coralia BlueBay Grand Esmeralda 5* offre une prestation de qualité pour des vacances en famille, en couple ou entre amis, au soleil à la découverte des richesses culturelles et gastronomiques mexicaines.\r\n', '- les vols aller et retour vers le Mexique <br/>\r\n- l’accueil et l’assistance<br/>\r\n- les taxes aéroportuaires et surcharges carburant (soumis à variation)<br/>\r\n- les transferts aller et retour aéroport/hôtel<br/>\r\n- l’hébergement en chambre double pour la durée du séjour<br/>\r\n- la pension de base', '- les frais de dossier<br/>\r\n- les assurances complémentaires facultatives<br/>\r\n- les prestations payantes (activités, excursions…)<br/>\r\n- les dépenses personnelles<br/>\r\n- les pourboires usuels<br/>\r\n- les éventuelles taxes de sortie du territoire, taxes de séjours (à régler sur place à l’hôtel) et visa d’entrée<br/>', '1868', '1966'),
(4, 'dr_pool.jpg', 'dr_home.jpg', 'rb_recept.jpg', 'dr_resto.jpg', 'dr_room.jpg', '- Une immense plage privée <br/>\r\n- Un magnifique jardin tropical <br/>\r\n- Des chambres spacieuses', '<h2>Situation</h2>\r\n\r\nLe Grand Bahia Principe San Juan 5* se trouve sur la Côte Nord de la République Dominicaine à 20 minutes de Rio San Juan et 46 km de Cabarete. L´hôtel est bordé par une plage privée de 1.5 km de long, protégée par un récif de corail. Il comprend une zone de plus de 150 hectares entourée d\'une végétation tropicale exubérante avec des palétuviers. Il dispose de nombreux jardins tropicaux traversés par des ruelles où les clients peuvent se promener et sentir en même temps la brise marine et la fraîcheur de l\'environnement naturel. L’hôtel se situe environ à 1h30 de l’aéroport de Samana et 3h de l’aéroport international de Saint Domingue. <br/><br/>\r\n\r\n<h2>Hebergement</h2>\r\n\r\nL’hôtel dispose de 608 chambres en deux catégories avec un balcon ou une terrasse <br/>offrant une vue sur la plage de sable, le jardin de palmiers et l’océan :<br/>\r\n-    Les chambres standards : 3 adultes ou 2 adultes + 2 enfants, avec 1 lit King-size ou 2 lites doubles.<br/>\r\n-    Les suites : 5 adultes maximum, avec 1 lit King-size.<br/><br/>\r\n\r\nElles sont toutes équipées du confort modernes : climatisation, ventilateur de plafond, téléphone, réveil, télévision LCD, minibar avec des boissons sans alcool, eau et bière remplacé quotidiennement, wifi dans l’hôtel (1h/jour/personne), salle de bains avec douche-baignoire, miroir, sèche-cheveux et produits de bain. <br/>\r\nLes suites ont en plus : un canapé-lit, un salon, une cafetière et une bouilloire, serviette de piscine et de plage, peignoirs et chaussons, parapluie, presse internationale.<br/><br/>\r\n\r\n<u>En supplément :</u> appels extérieurs, coffre-fort, wifi dans la chambre.<br/><br/>\r\n\r\n<h2>Restauration</h2>\r\n\r\nDans le cadre de la formule tout inclus, vous pourrez savourer vos repas sous forme de buffets au restaurant principal La Brisa : <br/>\r\n-    Petit déjeuners continental servi de 07h00 à 10h30*.<br/>\r\n-    Déjeuner servi de 13h00 à15h00*, vin à volonté.<br/>\r\n-    Dîner servi de 19h00 à 22h00*, vin à volonté.<br/>\r\n-    Snack nocturne de 23h00 à 07h00*.<br/><br/>\r\n\r\nIl existe aussi 3 restaurants à la carte : trois dîners vous sont offerts :<br/>\r\n-    Takara Asian : cuisine japonaise alternative.<br/>\r\n-    Dolce Vita : cuisine italienne traditionnelle.<br/>\r\n-    Orquidea : cuisine gastronomique.<br/>\r\nRepas spéciaux disponibles pour les clients qui suivent un régime particulier. <br/>\r\nPour des petites faims, est également à votre disposition un snack bar : grill, sandwich et fruits tropicaux.  <br/><br/>\r\n\r\n<u>Différents bars sont aussi à votre disposition :</u><br/>\r\n-    Pool bars en bordure de piscine.<br/>\r\n-    Beach Bars en bordure de plage.<br/>\r\nBoissons locales incluses dans votre formule de 10h00 à 02h00*.<br/><br/>\r\n\r\n*Les horaires sont communiqués à titre indicatif.<br/><br/>\r\n\r\n<h2>Equipements et Activités</h2>\r\n\r\nLa plage du resort, longue de 1,5 kilomètre, vous est réservée en exclusivité.<br/>\r\nL’hôtel possède deux piscines dont une principale en forme de lac et deux petits bassins pour les enfants.<br/>\r\nDans la journée vous aurez tout loisir de vous divertir grâce au programme varié : <br/>tennis (1h/jour/client), tennis de table, catamaran (1h/jour/client), beach-volley, canoë (1h/jour/client), windsurf (1h/jour/client), équipement de snorkelling (1h/jour/client), aquagym, stretching, aérobic, water-polo, jeux de fléchettes, volley-ball, mini-golf, football, basket-ball, pétanque, fitness (gym douce et cardio).<br/>\r\nEn supplément : plongée sous-marine, équitation.<br/><br/>\r\n\r\nPour vous détendre au maximum un spa de luxe en parfaite harmonie avec la nature et à la décoration d\'inspiration dominicaine (bois, bambou et toits en chaume de canne) vous proposera des massages caribéens, soins du visage et du corps à base de produits naturels et biologiques de la région : chocolat pur, aloe vera, noix de coco, etc.<br/><br/>\r\n\r\nLa discothèque vous attend également chaque soir pour vous faire danser jusqu\'à 2h du matin. <br/><br/>\r\n\r\n<u>Services :</u> parking, location de voitures, salon de coiffure, boutiques, blanchisserie, bureau de change, accès internet 1h/jour uniquement à la réception, accès aux personnes handicapées.<br/><br/>\r\n\r\n<h2>Notre avis</h2>\r\n\r\nLe Bahia Principe San Juan 5*, entouré de superbes jardins tropicaux, se trouve sur une belle et vaste plage qui arbore le prestigieux Pavillon bleu. Vous y recevrez un service inégalable grâce à des chambres modernes, une offre gastronomique internationale de grande qualité et un grand choix d’activités qui vous permettrons de profiter pleinement de vos vacances.', '- les vols aller et retour vers la République Dominicaine<br/>\r\n- l’accueil et l’assistance <br/>\r\n- les taxes aéroportuaires et surcharges carburant (soumis à variation) <br/>\r\n- les transferts aller et retour aéroport/hôtel <br/>\r\n- l’hébergement en chambre double pour la durée du séjour <br/>\r\n- la pension de base<br/>\r\n- le dîner de Réveillon du Nouvel An', '- les frais de dossier <br/>\r\n- les assurances complémentaires facultatives <br/>\r\n les prestations payantes (activités, excursions…) <br/>\r\n- les dépenses personnelles <br/>\r\n- les pourboires usuels <br/>\r\n- les éventuelles taxes de sortie du territoire, taxes de séjours (à régler sur place à l’hôtel) et visa d’entrée', '2364', '2409'),
(5, 'maurice_img.jpg', 'maurice_home.jpg', 'maurice_desk.jpg', 'maurice_resto.jpg', 'maurice_room.jpg', '- Une petite structure </br>\r\n- Un cadre intimiste</br>\r\n- Une plage de sable fin</br>', '<h2>Situation</h2>\r\n\r\nSitué sur la côte Ouest de l’île Maurice, The Bay Hôtel se trouve à une heure de la capitale de l’île. Egalement à une heure de l’aéroport International, ce transfert vous permettra de découvrir la campagne Mauricienne avant d’appréciez l’emplacement privilégié de l’hôtel. Faisant face à la Baie de Tamarin, il est niché dans une petite crique et donne directement sur la plage de sable fin « La Preneuse ». </br>\r\nA seulement 5 minutes de marche de l’hôtel, le village de Rivière Noire vous ravira par ses bou-tiques, ses bars et ses restaurants.</br></br>\r\n\r\n<h2>Hébergement</h2>\r\n\r\nPetite structure de charme, les 16 chambres de l’hôtel sont décorés avec goût et élégance ; la modernité des couleurs y rappel l’océan Indien et ses lagons turquoises. Spacieuses et lumi-neuses, les chambres disposent de tout le confort nécessaire pour passer d’agréables vacances : climatisation, télévision par satellite, ligne téléphonique directe, coffre fort individuel et mini bar, Wi-Fi gratuit La salle de bain avec douche et sèche-cheveux et les toilettes sont séparées.</br>\r\nLes chambre supérieur et les suites disposent également d’un balcon/ terrasse et d’un service thé/café.</br></br>\r\n\r\n<h2>Restauration</h2>\r\n\r\nIdéalement situé dans le petit jardin de The Bay entouré par la piscine & la mer, le restaurant marie avec bonheur les plaisirs des yeux et du palais. Terrasse ouverte sur la mer, le restaurant offre une atmosphère discrète et conviviale du petit déjeuner au dîner.</br>\r\nProposé en formule demi-pension, au petit-déjeuner vous aurez le choix entre l’option continen-tale et anglaise,  et un menu vous sera proposé tous les soirs comprenant une entrée, un plat et un dessert. Le chef se fera un plaisir de vous proposer une alternative en cas d’allergie ou de régime alimentaire différent.</br></br>\r\n\r\nPour le déjeuner (en supplément de la formule demi-pension) la carte offre un choix de snacks, de salades, de plats locaux et de grillades. </br></br>\r\n\r\nLe vendredi et le samedi soir des musiciens accompagnent le dîner.</br></br>\r\n\r\nLe restaurant est ouvert tous les jours comme suit:</br>\r\nLe petit-déjeuner de 7h30 à10h</br>\r\nLe déjeuner de12h à 15h</br>\r\nLe dîner de 18h30 à 22h</br></br>\r\n\r\nEn terrasse entre piscine et lagon, le bar de The Bay offre le cadre idéal pour un moment de dé-tente.</br>\r\nEn journée les cocktails sont accompagnés d’une magnifique vue sur l’océan Indien. Le soir de délicieux canapés aux saveurs locales préparés par le chef vous mettent en appétit à l’heure de l’apéritif.</br>\r\nLe bar offre un large choix de cocktails, malts, spiritueux et une très belle carte des vins. </br>\r\nLe bar est ouvert de 10h30 à 23h30.</br>\r\nL’accès Wi-Fi est offert à tous les clients.</br></br>\r\n\r\n<h2>Equipements et activités</h2>\r\n\r\nPar son emplacement privilégié en bordure de la plage « la Preneuse », The Bay hôtel et son accès direct au lagon turquoise fera la joie des baigneurs.</br>\r\nL’hôtel dispose également d’une belle piscine avec vue dégagée sur la plage. Si vous souhaitez vous détendre, l’hôtel propose un choix de massages et de soins beauté avec des thérapeutes indépendants.</br>\r\nProfitez de sa situation sur la côte Ouest, pour admirer des plus beaux couchers de soleil de l’île Maurice…</br>\r\nBon point de départ pour de nombreuses activités, vous trouverez à proximité de l’hôtel et avec participation, la possibilité de faire du tennis, du squash, du tir à l’arc, des descente en rappel, de la pêche au gros, des ballades en kayak, du golf (avec 3 cours de 18 trous), de l’équitation, du quad, du Kite-surf, de la planche à voile, du ski nautique ou du shopping.</br>\r\nDes activités (avec supplément) sont également proposées par l’hôtel tel que le Yoga sur la plage.</br></br>\r\n\r\n<h2>Notre avis</h2>\r\n\r\nOuvert depuis juillet 1997, The Bay Hôtel est le lieu idéal pour les personnes qui fuient les struc-tures tout inclus et recherchent la convivialité et la tranquillité.</br> \r\nL’emplacement exceptionnel sur la très belle plage de sable fin et le raffinement de l’hôtel vous permettront de passer des vacances découvertes en toute sérénité.', '- les vols aller et retour vers l\'Ile Maurice </br>\r\n- l’accueil et l’assistance </br>\r\n- les taxes aéroportuaires et surcharges carburant (soumis à variation) </br>\r\n- les transferts aller et retour aéroport/hôtel </br>\r\n- l’hébergement en chambre double pour la durée du séjour </br>\r\n- la pension de base', '- les frais de dossier </br>\r\n- les assurances complémentaires facultatives </br>\r\n- les prestations payantes (activités, excursions…) </br>\r\n- les dépenses personnelles </br>\r\n- les pourboires usuels </br>\r\n- les éventuelles taxes de sortie du territoire, taxes de séjours (à régler sur place à l’hôtel) et visa d’entrée', '3290', '3348'),
(6, 'malte_img.jpg', 'malte_home.jpg', 'malte_desk.jpg', 'malte_resto.jpg', 'malte_room.jpg', 'Imaginez… <br/>\r\nL’un des plus grands complexes de l’île. <br/>\r\nSitué en bord de mer et à proximité du centre-ville. <br/>\r\nNe manquez pas le casino de l’hôtel !! ', '<h2>Votre hôtel :</h2>\r\n\r\nLe complexe hôtelier Dolmen à Malte est l’un des hôtels les plus modernes de l’île. Son nom « DOLMEN » lui a été donné, en grande partie, à cause des restes archéologiques situés dans le jardin de l’hôtel, et autour desquels l’hôtel a été construit.<br/>\r\nSitué en bord de mer, dans un magnifique panorama surplombant la Baie de St. Paul, le complexe hôtelier Dolmen à Malte est un hôtel 4 étoiles, comptant de nombreux équipements extérieurs, de grandes terrasses et jardins.<br/>\r\nL’hôtel inclut également un Casino, un restaurant à la carte, un bar, une cafétéria, un club de la plage type Caraïbes comprenant un bar, une discothèque et le restaurant Batuluban (uniquement ouvert l’été), un bar à vin vintage, un bar près de la piscine (été uniquement) et une aire de stationnement.<br/><br/>\r\n\r\n<h2>Votre confort :</h2>\r\n\r\nLe Dolmen se compose de 375 chambres et 38 suites, toutes équipées de climatisation, salle de bains privée avec baignoire ou douche, sèche-cheveux, télévision par satellite, coffre-fort (payant), téléphone avec ligne directe, réfrigérateur et accès WiFi (gratuit). La majorité des chambres possèdent un balcon ou une terrasse.<br/>\r\nPossibilité de chambres vue mer en supplément.<br/><br/>\r\n \r\n<u>Votre Restauration et formule « petit déjeuner » :</u><br/><br/>\r\n\r\nVous prendrez votre petit déjeuner sous forme de buffet au restaurant principal.<br/>\r\nAvec participation, vous avez la possibilité de séjourner en formule « demi-pension » et de profiter des dîners au sein de l’hôtel (boissons incluses au dîner : vin local, bière, eau et sodas).<br/><br/>\r\n\r\n<h2>Vos activités et loisirs :</h2>\r\n\r\n-4 piscines extérieures, dont 1 piscine d’eau de mer et 1 piscine pour enfants (de mai à octobre) et  1 piscine couverte chauffée.<br/>\r\n-Terrasse aménagée avec chaises longues et parasols.<br/>\r\n-chaises longues et parasol à disposition à la plage, payants.<br/>\r\n-Prêt de serviettes (sous caution).<br/>\r\n-2 cours de tennis.<br/>\r\n-Centre de loisirs incluant : piscine intérieure chauffée (eau fraiche),  appareils de musculation, appareils cardiovasculaires informatisés, de nombreuses altères gratuites, sauna, douches, toilettes et vestiaires.<br/>\r\n-Des divertissements organisés tous les soirs incluent des prestations en direct, des nuits country & western, des spectacles de cabaret, les meilleurs chanteurs locaux et autre. Des animations sont également organisées toute l’année. Les programmes varient selon les saisons et les périodes de fêtes. Pendant l\'été, l’équipe d’animation organise également des spectacles le soir comme des spectacles de cabaret, des comédies musicales et pyrotechniques.<br/><br/>\r\n\r\n<u>Avec participation et selon la saison :</u> Pendant la période estivale (Mai à octobre) de nombreux sports nautiques sont proposés au Club privé de la plage (le rocky) qui possède un centre de plongée libre et de grandes terrasses de bronzage.\r\nAvec participation :<br/>\r\nle CASINO de l’hôtel, est ouvert de 10h à 6h et comprend quatre halls. Le premier est l’ « Oracle casino Pasta & Grill » dans lequel une nourriture de type bistro est servie au déjeuner et au dîner, le second est réservé aux machines à sous (178 machines à sous incluant le dernier modèle de roulette automatique), le troisième abrite des tables de jeu et le dernier est réservé aux fumeurs et comprend diverses machines à sous et tables de jeu ainsi qu\'un bar complètement équipé. Les parieurs auront le choix entre 12 tables comprenant entre autre une table de Roulette américaine, de Black Jack et diverses tables de Poker. Des tournois de Texas hold’em sont organisés 5 nuits par semaine et les jeux d’argent sont ouverts sur demande.<br/><br/>\r\n\r\nAMAZONIA comprend un club de plage type caribéen, possédant 2 discothèques, l’une principale et l’autre privée. Un DJ étranger participe régulièrement et l’entrée peut être payante.<br/><br/>\r\n\r\n<u>Votre moment de détente :</u><br/>\r\n\r\nUn espace pour vous ressourcer et vous détendre avec sauna, bain à remous, et avec participation, soins et massages.<br/><br/>\r\n\r\n<u>Rien que pour vos enfants :</u><br/>\r\n\r\n-Bassin pour les enfants (été uniquement).<br/>\r\n-Mini-club international pour les enfants de 4 à 12 ans ouvert 7j/7 de 10h00 à 12h00 et de 14h00 à 16h00 (de juin à septembre). Nous organisons également des activités pour les enfants du club pendant les périodes de fêtes et tous les dimanches pendant l’hiver.<br/>\r\n-Garde d’enfants.<br/>\r\n-Chaises hautes.<br/>\r\n-Des repas et des dîners plus tôt pour les enfants sont également disponibles sur demande.<br/><br/>\r\n \r\n<h2>Vos excursions :</h2>\r\n\r\n \r\n<b>La Valette (1/2 journée - 36€ par personne) :</b><br/><br/>\r\n\r\nNotre visite guidée à travers les rues animées de La Valette vous permettra de découvrir la beauté et le charme de cette ville fortifiée construite par les chevaliers en 1566. L’Ordre de Saint-Jean a régné sur les îles pendant 268 ans et a laissé un héritage culturel unique dont La Valette en est le premier exemple. Nous allons d’abord profiter d’une vue imprenable sur le Grand Port et les jardins d’Upper Barracca. Puis nous visitons la Co-Cathédrale St. Jean, y compris les chefs-d’oeuvre du Caravage dans l’Oratoire, les tapisseries flamandes et le musée de l’église. La Cathédrale abrite un des sols<br/>\r\nles plus exceptionnels en marqueterie de marbre au monde. En descendant rue de la République, vous passerez devant le Palais des Grands Maîtres et la place Saint Georges récemment restaurée avant de regarder The Malta Experience, un spectacle audiovisuel où l’histoire de Malte à travers les siècles est\r\nramenée à la vie!<br/>\r\n\r\n<b>Mdina (journée entière – 47€ par personne) :</b><br/><br/>\r\n\r\nL’excursion d’aujourd’hui nous emmène dans la partie centrale de Malte, dominée par la cité médiévale fortifiée de Mdina, ancienne capitale de Malte. Les rues étroites et tortueuses, qui remontent à l’époque médiévale, conduisent à des bastions imposants, offrant un magnifique panorama sur l’île. Sur le chemin, vous découvrirez différents styles d’architecture, y compris des palais siculo-normand et d’autres bâtiments baroques historiquement importants. Le temps semble s’être arrêté dans cette «cité silencieuse», qui est un paradis pour les cinéastes. En quittant Mdina par la Porte des Grecs, nous visitons les catacombes des premiers chrétiens à Rabat avant de nous diriger vers les falaises de Dingli. Après le déjeuner, nous allons visiter le jardin botanique de San Anton situé près du palais présidentiel que vous pourrez admirer de l’extérieur. Ensuite nous ferons un arrêt au centre d’artisanat à Ta ‘Qali et finalement vous serez surpris par la majestueuse coupole de l’église  de Mosta, l’une des plus grandes d’Europe.<br/>\r\nLe déjeuner est inclus dans le prix de la visite.<br/><br/>\r\n\r\n<b>Mdina (1/2 journée – 24€ par personne) :</b><br/><br/>\r\n\r\nUne demi-journée pour découvrir la ville médiévale de Mdina, l’ancienne capitale de Malte. Les rues étroites et tortueuses, qui remontent à la période arabe, conduisent à des bastions imposants, offrants une vue panoramique sur les îles. Sur le chemin, vous découvrirez différents styles d’architecture, y compris les palais médiévaux siculo-normand et d’autres édifices baroques historiquement importants.<br/>\r\nLe temps semble s’être arrêté dans cette «cité silencieuse», qui est un paradis pour les cinéastes. En quittant Mdina par la Porte de Grecs, nous nous dirigeons vers les falaises de Dingli. La visite se termine par une visite au jardin botanique de San Anton situé près du palais présidentiel, que vous pourrez admirer de l’extérieur.<br/><br/>\r\n\r\n<b>Malte sous les étoiles (soir en août et septembre – 25€ par personne) :</b><br/><br/>\r\n\r\nNous parcourons les fortifications de La Valette, qui sont magnifiquement éclairées la nuit, et ensuite nous nous arrêtons aux quais du Grand Port de la Valette. Construits à l’époque du Grand Maître Pinto, cette zone prend viela nuit, attirant des touristes et des Maltais qui fréquentent ses nombreux bars et restaurants. Ici vous aurez du temps libre. Découvrez Rabat au centre de l’île et marchez le long des rues étroites et sinueuses avant d’atteindre Mdina, l’ancienne capitale silencieuse de Malte - la nuit une ville mystérieuse qui semble si différente de ce qu’elle paraît pendant la journée. Une ville de palais et d’églises, dans le passé habitée par des nobles, son histoire remonte aux temps des Phéniciens, les Romains, les Normands, et les Chevaliers de St. Jean. Du haut des remparts de Mdina, vous aurez devant vous une vue étincelante de pratiquement la moitié de l’île.<br/><br/>\r\n\r\n<b>Les trois cités (matin - ½ journée – 28€ par personne) :</b><br/><br/>\r\n\r\nDécouvrez la région du Grand Port en face de La Valette connu sous le nom des Trois Cités - Vittoriosa, Cospicua et Senglea, où les Chevaliers de Saint-Jean s’installèrent d’abord en 1530. Traversant Cospicua nous atteignons Vittoriosa pour une promenade à travers les rues étroites à l’ombre des bâtiments historiques, et des premières auberges des Chevaliers. A Vittoriosa nous prenons un bateau typique (dghajsa) pour une visite des calanques du port. Du jardin de Senglea, situé à la pointe de la péninsule, profitez d’une vue imprenable à 360 ° sur le Grand Port, dont l’impressionnant Fort Saint-Ange, d’où le Grand Maître La Valette a mené la défense de l’île pendant le Grand Siège de 1565.\r\n\r\n\r\n', '-Le vol Aller/Retour France/Malte/France à bord d\'un vol spécial ou régulier.<br/>\r\n-Les transferts aéroport/hôtel/aéroport.<br/>\r\n-Le séjour selon la durée choisie en hôtel de séjour selon caractéristiques décrites plus haut.<br/>\r\n-Le type d\'hébergement et la formule de restauration sélectionnés.<br/>\r\n-Les taxes aériennes et aéroportuaires.<br/>\r\n-les bagages pour les séjours de 7 nuits et plus.<br/>\r\n-l’assistance sur place.<br/>\r\n-la taxe de séjour.', '-Les visites et toutes excursions optionnelles.<br/>\r\n-Le supplément chambre individuelle ou logement supérieur.<br/>\r\n-Les boissons et repas hors forfaits.<br/>\r\n-Toutes dépenses personnelles.<br/>\r\n-L’Eco Taxe : au titre de la contribution environnementale, il est demandé de régler sur place 0.50 €/nuit (maximum 5€/séjour) pour toute personne âgée de plus de 18 ans.<br/>\r\n-Les assurances facultatives.<br/>\r\n-Les hausses éventuelles de taxes aéroportuaires et surcharges carburant / compagnies.<br/>\r\n-les bagages pour les séjours inférieurs à 7 nuits.', '998', '1052'),
(7, 'bali_img.jpg', 'bali_home.jpg', 'bali_desk.jpg', 'bali_resto.jpg', 'bali_room.jpg', '- Un endroit privilégié<br/>\r\n- Un hébergement typiquement balinais<br/>\r\n- Au coeur des rizières', '<h2>Situation</h2>\r\n\r\nUnited Colors of Bali, situé à Canggu, est l’endroit rêvé pour se ressourcer. Le Resort bénéficie d’une situation exceptionnelle au milieu des rizières en terrasses faisant face au fameux couché de soleil de Bali. Il est à seulement 10 minutes des plages, 15 minutes des zones touristiques de Semyniak et Oberoi et à 40 minutes de l’aéroport.<br/><br/>\r\n\r\n<h2>Hébergement</h2>\r\n\r\nImplanté sur 2200 m², United Colors of Bali se compose de 9 villas-hôtel basées sur le thème des couleurs. Vous serez logés dans une Villa 1 chambre pouvant accueillir maximum 3 adultes ou 2 adultes + 1 enfant. Bénéficiant d\'une terrasse et d\'une vue sur le jardin, chaque villa a une superficie de 70 m2 et dispose de toutes les commodités essentielles pour un séjour de qualité. Inspirés de l\'architecture balinaise, elles sont construites et conçues avec des matériaux locaux pour préserver l\'environnement.. Elles sont climatisées et comprennent un coffre-fort, une armoire, un coin salon, un coin repas ainsi qu\'une kitchenette équipée d\'un réfrigérateur, d\'une bouilloire électrique et d\'ustensiles de cuisine. Les salles de bains privatives semi-ouvertes comportent une douche avec eau chaude et froide. Connexion Wi-fi gratuite dans les villas.<br/><br/>\r\n\r\n<h2>Restauration</h2>\r\n\r\nDans le cadre de votre formule, vous bénéficiez des petits déjeuners. Chaque matin un riche petit déjeuner intercontinental vous sera servi par l\'hôtel dans un cadre tranquille et relaxant. Cette formule vous permettra de profiter de votre journée complète à la découverte de Bali.<br/>\r\nPour vous rafraichir, un bar/salon et un bar en bord de piscine sont à votre disposition.<br/><br/>\r\n\r\n<h2>Equipements et activités</h2>\r\n\r\nUne piscine est à votre disposition autour de laquelle vous profiterez des chaises longues et parasols entourées des senteurs et couleurs d’un magnifique jardin tropical.<br/><br/>\r\n\r\nVous pourrez bénéficier en supplément d\'un massage ou louer un scooter pour partir à la découverte de la région. L\'United Colors of Bali assure également des services de blanchisserie, de location de voitures et de garde d\'enfants. Pour plus de commodité, l\'établissement possède aussi un bureau d\'excursions et un parking privé gratuit.<br/><br/>\r\n\r\nDe nombreuses acticités sont possibles à proximité moyennant supplément : Surf, Golf, VTT, plongée<br/><br/>\r\n\r\n<h2>Notre avis</h2>\r\n\r\nAu United Colors of Bali 3* vous bénéficiez d’un site à taille humaine où la gentillesse et le sourire du personnel vous plongent directement au cœur de Bali pour des vacances inoubliables.', '- les vols aller et retour vers l\'Indonésie<br/>\r\n- l’accueil et l’assistance<br/>\r\n- les taxes aéroportuaires et surcharges carburant (soumis à variation)<br/>\r\n- les transferts aller et retour aéroport/hôtel<br/>\r\n- l’hébergement en chambre double pour la durée du séjour<br/>\r\n- la pension de base', '- les frais de dossier<br/>\r\n- les assurances complémentaires facultatives<br/>\r\n- les prestations payantes (activités, excursions…)<br/>\r\n- les dépenses personnelles<br/>\r\n- les pourboires usuels<br/>\r\n- les éventuelles taxes de sortie du territoire, taxes de séjours (à régler sur place à l’hôtel) et visa d’entrée', '2144', '2168');
INSERT INTO `trip` (`idTrip`, `img`, `home_img`, `desk_img`, `resto_img`, `room_img`, `essential`, `detail`, `price_have`, `price_dont`, `priceRoom`, `priceRoomSup`) VALUES
(8, 'kenya_img.jpg', 'kenya_home.jpg', 'kenya_desk.jpg', 'kenya_resto.jpg', 'kenya_room.jpg', 'Le Kenya, terre des Masaïs et de la savane à perte de vue, offre d\'innombrables opportunités pour la relaxation, les loisirs, le sport et l\'aventure à travers un paysage exceptionnel de montagnes, déserts, prairies et côtes littorales verdoyantes. <br/><br/>\r\n\r\nLe Kenya abrite plus de 60 parcs naturels et réserves parmi lesquels ceux de Amboselli, ...', '<h2>Situation</h2>\r\n\r\nLe Kenya, terre des Masaïs et de la savane à perte de vue, offre d\'innombrables opportunités pour la relaxation, les loisirs, le sport et l\'aventure à travers un paysage exceptionnel de montagnes, déserts, prairies et côtes littorales verdoyantes.<br/><br/> \r\n\r\nLe Kenya abrite plus de 60 parcs naturels et réserves parmi lesquels ceux de Amboselli, Tsavo et Masaï Mara sont les plus connus. Le Kenya offre l\'occasion de faire l\'expérience unique d\'un Safari et d\'admirer des animaux sauvages. <br/><br/>\r\n\r\nAvec son littoral ensoleillé face à l\'Océan Indien, ses longues plages de sables entourées de récifs coralliens, ses rues animées, ses restaurants et ses boutiques, Malindi offre un charme indiscutable. <br/><br/>\r\n\r\nL\'hôtel Neptune Village Beach Resort & Spa 4* (normes locales) est idéalement situé le long des magnifiques plages de Diani Beach. C\'est là que vous ressentirez au mieux l\'hospitalité africaine. Un bel ensemble que forment l\'hôtel avec les chambres sous leurs toits en makuti, les restaurants, les bars et le très grand jardin tropical. Il est situé sur le plus large front de mer à Mombasa. Profitez de l\'offre tout compris. Situé à environ 40 km au sud de Mombasa, environ 45 km de l\'aéroport international Moi et à seulement 8 km de l\'aéroport local Ukunda.<br/><br/>\r\n\r\n<h2>Logement</h2>\r\n\r\nL\'hôtel possède 165 chambres, réparties dans des bungalows de type traditionnel (maximum 4 chambres par bungalow). La chambre supérieure avec accès direct vers le jardin. Chambre équipée d\'air conditionné, d\'une moustiquaire, d\'une télévision satellite, d\'un petit réfrigérateur, d\'un coffre-fort, possibilité de café et thé en chambre, terrasse et Wifi gratuit. La salle de bain dispose d\'une douche, sèche-cheveux et toilettes. <br/><br/>\r\n\r\nVous serez logés en chambre Supérieure jardin.<br/><br/>\r\n\r\n<h2>Conditions de pension</h2>\r\n\r\nLa disponibilité des différents modes de pension n\'est pas garantie . La disponibilité de la pension choisie sera confirmée au moment de la réservation.<br/><br/>\r\n\r\n<h3>Tout compris</h3>\r\n\r\nVous serez logés en formule tout-inclus servis sous forme de buffet au restaurant principal de l\'hôtel. <br/><br/>\r\n\r\n<h2>Restaurants: </h2>\r\n\r\n<u>Karamu (restaurant principal)</u> <br/>\r\nLe restaurant ‘Karamu\' propose un buffet pour les petits déjeuners de 7h à 10h , qui peut être prolongé jusqu\'à midi, sur demande, le lunch est servi de midi à 15h et le dîner de 19h à 22h sous différents thèmes. Pendant les repas, des boissons fraîches, jus de fruits, eaux, vin maison, bière locale, café et thé sont prévus. <br/><br/>\r\n\r\n<u>Bustani (restaurant à la carte)</u> <br/>\r\nSitué à côté de la piscine, le restaurant Bustani propose une carte élaborée de snacks et des plats à la carte, avec un choix de spécialités de fruits de mer comme des homards, gambas et poisson frais sur commande. Notre responsable du Restaurant se fera un plaisir de prendre votre réservation. <br/><br/>\r\n\r\n<u>Tea and Coffee Service</u> <br/>\r\nLe café et thé est servi durant toute la journée à côté du restaurant Bustani avec sandwiches, cakes et cookies à différents moments de la journée. <br/><br/>\r\n\r\nL\'hôtel possède 2 bars, un bar principal ‘Furaha\' et un autre près de la piscine ‘Tempo\'. Un choix d\'alcools locaux, jus de fruits frais, cocktails, bières et boissons fraîches de 10h à minuit. <br/><br/>\r\n\r\nAlcools internationaux disponibles moyennant un coût supplémentaire.<br/><br/>\r\n\r\n<h2>Equipements</h2>\r\n\r\nL\'hôtel dispose de coffre-fort/consigne à la réception, bibliothèque, réception ouverte 24 h/24, services de concierge, service de nettoyage à sec/blanchisserie.<br/><br/>\r\n\r\n<h2>Sports et loisirs</h2>\r\n\r\nL\'hôtel Neptune Village Beach Resort & Spa est entouré d\'un grand jardin , le long de la rive de l\'océan. Grande piscine d\'eau douce avec un espace séparé pour les enfants, centre de sports nautiques et Kids club pour les enfants. Il y a un billard, table de tennis de table, fléchettes, mini-golf (putting), aquagym, water volley ball, volley ball sur la plage ainsi que 2 terrains de tennis éclairés. Golf de 18 trous dans les environs (Leisure Lodge Club).<br/><br/>\r\n\r\n<h2>Sports et loisirs payants</h2>\r\n\r\n<u>L\'hôtel propose un \"Spa 5enses Sea\" :</u> \r\nAu Spa Sea 5enses, nous offrons un environnement accueillant et relaxant et souhaitons vous faire profiter de toutes les facilités au maximum. Nous traitons vos 5 sens lors d\'expériences extraordinaires d\'arômes, de sons ultra-reposants, de touches magiques et le plus important, avec des soins professionnels, le tout avec une vue surprenante sur l\'océan, au centre du Neptune Village. <br/><br/>\r\n\r\n<u>Soins proposés :</u> 4 cabines de massages et de soins, une piscine et 2 bains à remous indépendants, hammam et sauna, afin que les clients puissent s\'y relaxer, avant ou après leurs soins. Nous proposons des soins du visage, des mains, des pieds, massages, épilation et soins cheveux.<br/><br/>\r\n\r\n<h2>Animation</h2>\r\n\r\nUne équipe d\'animateurs de l\'hôtel anime tous les jours petits et grands. Ils vous surprendront entre autre avec des défilés de mode, des shows acrobatiques, des danses africaines et bien plus. Pour les sportifs, un cours d\'aqua gym est prévu tous les jours. <br/>\r\nQuelques services vous seront proposé moyennant un supplément. <br/><br/>\r\n\r\n\r\n<h2>Sports et loisirs : </h2>\r\n\r\nL\'hôtel Neptune Village Beach Resort & Spa est entouré d\'un grand jardin , le long de la rive de l\'océan. Grande piscine d\'eau douce avec un espace séparé pour les enfants, centre de sports nautiques et Kids club pour les enfants. Il y a un billard, table de tennis de table, fléchettes, mini-golf (putting), aquagym, water volley ball, volley ball sur la plage ainsi que 2 terrains de tennis éclairés. Golf de 18 trous dans les environs (Leisure Lodge Club). \r\n\r\n', '- Les vols internationaux au départ de France/Mombasa A/R, <br/>\r\n- L\'accueil et l\'assistance, <br/>\r\n- Les transferts entre l\'aéroport et l\'hôtel sous réserve de disponibilité au moment de la réservation (information donnée sur le détail de la prestation, au cours de la réservation),<br/> \r\n- Le logement en chambre double standard selon la durée choisie, <br/>\r\n- La pension validée au moment de la réservation (selon sa disponibilité) : le type de pension sera repris sur vos documents de voyage, <br/>\r\n- Les taxes d\'aéroport (sujettes à évolution) et la taxe de solidarité (incluant la hausse carburant obligatoire sous réserve de hausse supplémentaire).', '- Les frais de dossier <br/>\r\n- L\'excédent bagages des sacs hors gabarit (à régler lors de l\'enregistrement à l\'aéroport), <br/>\r\n- Les assurances facultatives, <br/>\r\n- La taxe de sortie du territoire et/ou la carte touristique si existante, <br/>\r\n- Les activités payantes, <br/>\r\n- Les dépenses d\'ordre personnel, <br/>\r\n- Le visa et les frais de services obligatoires si existants, <br/>\r\n- Les pourboires usuels.', '2458', '3516'),
(9, 'turquie_img.jpg', 'turquie_home.jpg', 'turquie_pool.jpg', 'turquie_resto.jpg', 'turquie_room.jpg', ' - directement sur la plage, à flanc de coteau <br/>\r\n - détails de plage: plage privée de l\'hôtel; sable/galets; information sur la plage: label drapeau bleu <br/>\r\n - distance (environ) : de l\'aéroport Bodrum: 35 km, du centre ville/localité: 1 km, des commerces: 1 km, de restaurants/bars: 1 km <br/>', '<h2>Situation</h2>\r\n - directement sur la plage, à flanc de coteau<br/>\r\n - détails de plage: plage privée de l\'hôtel; sable/galets; information sur la plage: label drapeau bleu<br/>\r\n - distance (environ) : de l\'aéroport Bodrum: 35 km, du centre ville/localité: 1 km, des commerces: 1 km, de restaurants/bars: 1 km<br/><br/>\r\n\r\n<h2>Equipement</h2>\r\n\r\n - nombre de bâtiments annexes: 6, nombre d\' étages: 3<br/>\r\n - nombre total de chambres/logements: 212<br/>\r\n - hall d\'accueil, réception , 24h/24, coin salon, 4 ascenseur(s), coin internet (inclus), WIFI (inclus) dans l\'ensemble du bâtiment<br/>\r\n - nombre total de restaurants: 1<br/>\r\n - restaurant buffet avec espace intérieur , terrasse<br/>\r\n - nombre de bars: 1, type: bar lobby<br/>\r\n - coiffeur , magasin(s), salle(s) de réunion, espace TV<br/>\r\n - distance au prochain distributeur automatique de billets/DAB: 1 km<br/>\r\n - service de chambre (payant)<br/>\r\n - parking (selon disponibilité): dans le complexe hôtelier: inclus<br/>\r\n - cartes de crédit : VISA, Mastercard<br/>\r\n - liaisons en taxis et Dolmus disponibles.<br/><br/>\r\n\r\n<h2>Catégorie locale</h2>\r\n\r\n - convivial(e)<br/>\r\n - dimension de la chambre: 20 - 25 m²<br/>\r\n - situation: dans le bâtiment bungalow<br/>\r\n - salle(s) de bains/WC<br/>\r\n - TV sat., canal musical, coin salon, sèche-cheveux, téléphone, balcon ou terrasse<br/>\r\n - chaînes de télévision allemande: ZDF, RTL<br/>\r\n - WIFI (inclus)<br/>\r\n - coffre-fort (inclus)<br/>\r\n - mini-bar (payant)<br/>\r\n - climatisation (inclus), centralisé(e), à certaines heures, saisonnier<br/>\r\n - chauffage, saisonnier<br/>\r\n - possibilité à usage individuel (D1L)<br/>\r\n - possibilité de réserver d\'autres options: vue latérale sur la mer (DZK), à usage individuel (D1K)<br/><br/>\r\n\r\n<h2>Restauration</h2>\r\n\r\n - logement et petit-déjeuner (buffet)<br/><br/>\r\n\r\n<h2>Sports / Divertissements</h2>\r\n\r\n - activités sportives: centre de fitness (inclus)<br/>\r\n - sport de balle: billard (inclus); tennis (inclus, court en dur, éclairage: payant, location de l\'équipement: contre caution); tennis de table (inclus)<br/>\r\n - cabinets de soins (nombre : 4) <br/>\r\n - centre Spa: taille: moyen (101-500 m²)<br/>\r\n - piscine intérieure (inclus), eau douce, saisonnier<br/>\r\n - bain à remous (inclus)<br/>\r\n - hammam (inclus)<br/>\r\n - bain à vapeur (inclus)<br/>\r\n - sauna (inclus): sauna aromatique<br/>\r\n - services (payant): massages; soin de remise en forme et beauté<br/><br/>\r\n\r\n<h2>Enfants</h2>\r\n\r\n - chauffe-biberon (en demande)<br/>\r\n - chaise haute (en demande)<br/>\r\n - salle à langer<br/>\r\n', ' - Le vol aller / retour<br/>\r\n - Les taxes d\'aéroport.<br/>\r\n - Les transferts aéroport/hôtel/aéroport.<br/>\r\n - Le séjour de la durée choisie, dans l\'établissement retenu.<br/>\r\n - Le type d\'hébergement et la formule de restauration sélectionnés.<br/>\r\n - L\'assistance sur place.<br/>\r\n - Des documents de voyage.', ' - Les pourboires et dépenses personnelles<br/>\r\n - Les frais liés aux formalités d\'entrée dans le pays<br/>\r\n - Les repas et boissons non mentionnés<br/>\r\n - Les services et activités payants<br/>\r\n - Les éventuels suppléments à bord de l\'avion (collations et boissons sur certaines compagnies)<br/>\r\n - Les assurances facultatives<br/>\r\n - Les éléments non mentionnés dans « notre offre comprend »', '1208', '1273'),
(10, 'pb_img.jpg', 'pb_home.jpg', 'pb_desk.jpg', 'pb_resto.jpg', 'pb_room.jpg', '-À env. 10 min. à pied de Museumplein<br/>\r\n-L\'arrêt du tram à 5 min. à pied<br/>\r\n-Restaurant à la carte', '<h2>BIENVENUE CHEZ VOUS</h2>\r\n\r\n-dans le quartier Oud-Zuid\r\n-à env. 10 minutes de marche des rues commerçantes les plus chic d\'Amsterdam, du Rijksmuseum, du Stedelijkmuseum et du musée Van Gogh, du Concertgebouw et du Vondelpark<br/>\r\n-parking dans la rue (ticket à la réception, env. € 22 par 24 h; gratuit le dimanche)<br/>\r\n-tram: Lairessestraat (lijn 16), à env. 5 minutes à pied; ; Roelof Hartplein (lignes 5 et 24) à env. 10 min. à pied. <br/> <br/>\r\n\r\n<h2>Votre Chambre</h2>\r\n\r\n124 chambres aménagées avec goût dans un style moderne (4 étages) avec coin salon, wifi (payant), téléphone, TV-satellite, radio et réveil intégrés, minicoffre, minibar, possibilité de préparer du thé/café, fer et planche à repasser (à la réception), climatisation, sèche-cheveux, bain avec douche, WC, sortie de bain et pantoufles. Toutes les chambres sont non-fumeurs. Possibilité de chambres communicantes.<br/><br/>\r\n\r\nType 2A: chambre 2/3 personnes Deluxe. 3e personne de moins de 4 ans.<br/><br/>\r\n\r\nType 1A: chambre 1 personne Deluxe.<br/><br/>\r\n\r\n\r\n<h2>Les Restaurants</h2>\r\n\r\n-salle de petit-déjeuner<br/>\r\n-restaurant à la carte renommé où vous apprécierez une cuisine créative (fermé le dimanche)<br/>\r\n-bar convivial avec lounge et cheminée<br/>\r\n-petit-déjeuner buffet copieux<br/><br/>\r\n\r\n<h2>Les Services</h2>\r\n\r\n-réception<br/>\r\n-wifi (gratuit)<br/>\r\n-2 ascenseurs<br/>\r\n-valet parking<br/><br/>\r\n\r\n<h2>Ambiance et Sport</h2>\r\n\r\n-fitness<br/>\r\n-massage<br/>\r\n-institut de beauté<br/>\r\n-location des vélos<br/><br/>\r\n\r\n<h2>L\'Esprit de Famille</h2>\r\n\r\n-lit bébé (à signaler lors de la réservation)', '-taxe de séjour<br/><br/>\r\nDans le cadre d\'un séjour avec transport :Le transport, l\'hébergement, les repas selon la formule choisie et les services comme mentionnés sur la présente offre.Dans le cadre d\'un séjour en rendez-vous sur place :L’hébergement, les repas selon la formule choisie et les services comme mentionnés sur la présente offre.', 'Dans le cadre d\'un séjour avec ou sans transport :Les transferts aller/retour aéroport/lieu de séjour, l\'assurance voyage, les dépenses à caractère personnel, la taxe de séjour (sauf mention contraire).', '376', '411'),
(11, 'tenerife_img.jpg', 'tenerife_home.jpg', 'tenerife_desk.jpg', 'tenerife_resto.jpg', 'tenerife_room.jpg', 'Face à la mer, offrant une belle vue sur l\'île de la Gomera.\r\n', '<h2>BIENVENUE CHEZ VOUS</h2>\r\n\r\n<h2>Les chambres</h2>\r\n\r\n141 chambres rénovées réparties sur 3 étages. Toutes sont équipées de coffre (payant) et petit réfrigérateur (payants), TV satellite, Wi-fi (payant), ventilateur, téléphone, salle de bains avec sèche-cheveux, balcon ou terrasse.<br/><br/>\r\n\r\n<b>Type A :</b>\r\nChambre double vue mer. Possibilité de chambre double à usage individuel.<br/><br/>\r\n<b<Type B :</b>Chambre famille vue mer avec un salon avec canapé-lit et une chambre séparée.<br/><br/>\r\n<b>Type S :</b>\r\nNombre limité de chambre double à tarif promotionnel.<br/><br/>\r\n\r\n<h2>Au menu</h2>\r\n\r\nLa demi-pension. La formule Tout compris en option. 1 restaurant buffet de cuisine internationale et spécialités régionales, soirées à thème. 1 bar près de la piscine.<br/><br/>\r\n\r\n<h2>Les services</h2>\r\n\r\nWi-fi gratuit à la réception. Avec participation : service de blanchisserie.<br/><br/>\r\n\r\n<h2>AMBIANCE ET SPORT</h2>\r\n\r\n<h3>Activités pour tous</h3>\r\nSpectacles plusieurs fois par semaine.\r\n<h3>Les sports</h3>\r\nPétanque, tennis, tennis de table. Avec participation : billard.<br/>\r\nPiscine avec chaises longues et parasols, serviettes (pas de caution).', '-Le transport aérien aller et retour sur vol spécial ou régulier<br/>\r\n-Les transferts aéroport - hôtel - aéroport<br/>\r\n-Le logement selon chambre sélectionnée avec la pension selon la formule choisie. Par exemple, pour les séjours de 8 jours/7 nuits, la demi-pension comprend 7 petits déjeuners et 7 déjeuners ou dîners selon descriptif de l\'hôtel choisi, la pension complète et la formule \" Tout compris \" comprennent 7 petits déjeuners, 7 déjeuners et 7 dîners. Toutefois selon les horaires d\'arrivée et de départ, le premier et/ou le dernier repas pourront ne pas être servis.<br/>\r\n-L\'accueil et l\'assistance sur place, l\'accès aux services et infrastructures de l\'hôtel (sauf prestations facultatives en supplément)<br/>\r\n-Les frais de dossiers, les taxes d\'aéroport et les taxes hôtelières, les taxes de sûreté et redevances passagers.<br/>', '-Les assurances annulation, rapatriement et bagages.<br/>\r\n-Les dépenses personnelles, les pourboires.<br/>\r\n-Les taxes de séjour ou de sortie du territoire à régler sur place, les frais liés aux formalités (visas, vaccinations, passeport).<br/>\r\n-Les frais de dossier de l’agence.', '768', '803'),
(12, 'pc_img.jpg', 'pc_home.jpg', 'pc_desk.jpg', 'pc_resto.jpg', 'pc_room.jpg', '- Une plage paradisiaque<br/>\r\n- Une restauration variées<br/>\r\n- De nombreuses activités', '<h2>Situation</h2>\r\n\r\nLe Caribe Club Princess Beach Resort & Spa 4* se trouve à Punta Cana, la célèbre zone touristique du pays avec ses 40 km de plages, entre mer des Caraïbes et océan Atlantique, sur la côte Sud-Est de la République dominicaine. Situé dans un vaste jardin tropical abritant une mangrove, face à la plage de Bavaro, il est à 18 km de l’aéroport de Punta Cana, à 600 mètres du centre commercial Plaza Bávaro, à 300 mètres d’un parcours de Golf.<br/>\r\nCet hôtel est situé dans une zone offrant également un large de choix d’attractions culturelles : l\'église du XVIe siècle de San Dionisio, la basilique de Notre-Dame de Altagracia et celle de Higuey,…<br/><br/>\r\n\r\n<h2>Hébergement</h2>\r\n\r\nCet hôtel moderne compte au total 355 chambres. <br/>\r\nDurant votre séjour, vous serez hébergés en chambre Supérieure réparties sur plusieurs bâtiments.<br/>\r\nElles sont spacieuses avec un balcon, idéales pour les familles ou groupes d’amis. Occupation maximale : 4 personnes (soit 3 adultes + 1 enfant, soit 2 adultes + 2 enfants). <br/>\r\nElles disposent de toutes les commodités modernes : 2 lits jumeaux ou 1 lit King, salle de bain équipée avec douche et baignoire, sèche-cheveux, fer et planche, ventilateur de plafond, télévision par câble, minibar (approvisionné tous les jours en eau, boissons gazeuses), climatisation et téléphone. <br/>\r\nEn supplément : Coffre-fort, connexion Wifi dans la chambre.<br/><br/>\r\n\r\n<h2>Restauration</h2>\r\n\r\n<b>L’hôtel est en formule tout inclus. Les trois repas seront servis sous forme de buffet international au restaurant principal Higuero :</b><br/>\r\n-    Petit déjeuner : de 06h00 à 10h30*<br/>\r\n-    Déjeuner : de 12h30 à 15h00*<br/>\r\n-    Dîner : de 18h30 à 22h30*<br/><br/>\r\n\r\n<b>Dans le cadre de votre formule, vous profiterez également d’autres restaurants à la carte ouverts de 18h00 à 21h00* :</b><br/>\r\n-    Shangay Asian Restaurant cuisine asiatique <br/>\r\n-    Mamma Mia Restaurant cuisine italienne<br/>\r\n-    Restaurant Sombrero cuisine mexicaine<br/>\r\n-    Snack Bar La Matica avec sandwiches, hamburgers, hot-dogs, salades mixtes, chips et un large choix des fruits.<br/>\r\nEn supplément, deux restaurants gastronomiques : “La Cava” and “El Pescador”.<br/><br/>\r\n\r\n<b>Des bars seront aussi à votre dispositions proposant des boissons locales:</b><br/>\r\n-    Lobby Bar La Carreta<br/>\r\n-    Serenata Bar<br/>\r\n-    PoolBar Sunken<br/>\r\n-    Bar La Matica<br/>\r\nEn supplément : boissons internationales<br/><br/>\r\n\r\n*Les horaires sont communiqués à titre indicatif.<br/><br/>\r\n\r\n<h2>Equipements et activités</h2>\r\n\r\n<b>L’hôtel vous offre un large choix d’installations, de services ainsi que d’activités sportives :</b><br/>\r\n-    Piscine pour adultes et enfants (avec bar swim- up)<br/>\r\n-    Programmes d’animations le soir<br/>\r\n-    Discothèque Areito (entrée et boissons locales gratuites jusqu’à minuit)<br/>\r\n-    Salle de gym et bains à remous<br/>\r\n-    Leçon de plongée dans la piscine (une leçon par personne par jour)<br/>\r\n-    Sports nautiques non motorisés <br/>\r\n-    Tennis, mini-golf, tir à l’arc, vélos, jeux vidéo<br/>\r\n-    Kids club pour les enfants de 4 à 12 ans<br/>\r\n-    Teens club pour les adolescents de 13 à 17 ans<br/>\r\n-    Aire de jeux extérieure pour les enfants<br/>\r\n-    Programme quotidien d’activités sur la plage et à la piscine<br/>\r\n-    Connexion Wifi dans le hall d’accueil<br/>\r\n-    Une réception ouverte 24h/24<br/>\r\n-    Parking<br/><br/>\r\nL’accès aux installations de l’hôtel Tropical Princess, situé à proximité, est possible.<br/><br/>\r\n\r\n<b>Services avec supplément :</b> <br/>\r\n-    Salles de réunions et de congrès<br/>\r\n-    Princess Tower Casino (situé à Bavaro Princess) avec un service de navette gratuit\r\n-    Centre de Spa Metamorphosis <br/>\r\n-    Salon de coiffure<br/>\r\n-    Centre commercial : boutique, photographe, boutique de cadeaux et mini-marché<br/>\r\n-    Centre médical, ouvert 24/24h<br/>\r\n-    Service de chambre<br/>\r\n-    Garde d’enfants<br/>\r\n-    Internet en dehors de la zone de réception<br/>\r\n-    Forfaits de golf<br/>\r\n-    Sports nautiques motorisés<br/>\r\n-    Forfaits mariage<br/><br/>\r\n\r\n<h2>Notre avis</h2>\r\n\r\nIdéalement situé sur la plage de Bavaro, en tête des plus belles plages de l\'île, l’hôtel Caribe Club Princess Beach Resort & Spa 4* vous offre de magnifiques paysages et un large choix d’attractions culturelles et de sports nautiques.', '- les vols aller et retour versla République Dominicaine <br/>\r\n- l’accueil et l’assistance <br/>\r\n- les taxes aéroportuaires et surcharges carburant (soumis à variation) <br/>\r\n- les transferts aller et retour aéroport/hôtel <br/>\r\n- l’hébergement en chambre double pour la durée du séjour <br/>\r\n- la pension de base ', '- les frais de dossier <br/>\r\n- les assurances complémentaires facultatives <br/>\r\n- les prestations payantes (activités, excursions…) <br/>\r\n- les dépenses personnelles <br/>\r\n- les pourboires usuels <br/>\r\n- les éventuelles taxes de sortie du territoire, taxes de séjours (à régler sur place à l’hôtel) et visa d’entrée', '1692', '1727'),
(13, 'suede_img.jpg', 'suede_desk.jpg', 'suede_rooom.jpg', 'suede_resto.jpg', 'suede_room.jpg', 'Hôtel quatre étoiles soigné proposant des chambres confortables, un excellent service et de nombreux aménagements. Dans le célèbre quartier de Vasastaden.', '<h2>BIENVENUE CHEZ VOUS</h2>\r\n\r\n-à Vasastaden<br/>\r\n-à courte distance à pied d\'Odenplan, de Sankt Eriksplan et du parc Vasa<br/>\r\n-à env. 15 minutes de marche du centre commercial et de la rue commerçante par excellence, Drottninggatan<br/>\r\n-métro: Sankt Eriksplan (ligne verte) à env. 500 m<br/><br/>\r\n\r\n<h2>Votre Chambre</h2>\r\n\r\n215 chambres aménagées avec goût dans un style moderne (4 étages) avec coin salon, wifi (payant), téléphone, TV-satellite, radio et réveil intégrés, minicoffre, minibar, possibilité de préparer du thé/café, fer et planche à repasser (à la réception), climatisation, sèche-cheveux, bain avec douche, WC, sortie de bain et pantoufles. Toutes les chambres sont non-fumeurs. Possibilité de chambres communicantes.<br/><br/>\r\n\r\nType 2A: chambre 2/3 personnes Deluxe. 3e personne de moins de 4 ans.<br/><br/>\r\n\r\nType 1A: chambre 1 personne Deluxe.<br/><br/>\r\n\r\n\r\n<h2>Les Restaurants</h2>\r\n\r\n-salle de petit-déjeuner<br/>\r\n-restaurant à la carte renommé où vous apprécierez une cuisine créative (fermé le dimanche)<br/>\r\n-bar convivial avec lounge et cheminée<br/><br/>\r\n\r\n<h2>Les Restaurants</h2>\r\n\r\n-petit-déjeuner buffet copieux<br/><br/>\r\n\r\n<h2>Les Services</h2>\r\n\r\n-réception<br/>\r\n-wifi (gratuit)<br/>\r\n-2 ascenseurs<br/>\r\n-valet parking<br/><br/>\r\n\r\n<h2>Ambiance et Sport</h2>\r\n\r\n-fitness<br/>\r\n-massage<br/>\r\n-institut de beauté<br/>\r\n-location des vélos<br/><br/>\r\n\r\n<h2>L\'Esprit de Famille</h2>\r\n\r\n-lit bébé (à signaler lors de la réservation)', 'Dans le cadre d\'un séjour avec transport :Le transport, l\'hébergement, les repas selon la formule choisie et les services comme mentionnés sur la présente offre.Dans le cadre d\'un séjour en rendez-vous sur place :L’hébergement, les repas selon la formule choisie et les services comme mentionnés sur la présente offre.', 'Dans le cadre d\'un séjour avec ou sans transport :Les transferts aller/retour aéroport/lieu de séjour, l\'assurance voyage, les dépenses à caractère personnel, la taxe de séjour (sauf mention contraire).', '248', '282'),
(14, 'valais_img.jpg', 'valais_home.jpg', 'valais_rooom.jpg', 'valais_resto.jpg', 'valais_room.jpg', '-jusqu\'à 30% de réduction sur les skipass sur place<br/>\r\nen face du téléphérique<br/>\r\n-vue panoramique<br/>\r\n-wifi gratuit', '<h2>BIENVENUE CHEZ VOUS</h2>\r\n\r\n-à env. 1400 m d\'altitude<br/>\r\n-magnifique vue panoramique sur la vallée du Rhône<br/>\r\n-au pied de la télécabine et de la piste de ski<br/>\r\n-à env. 50 m de l\'école de ski<br/>\r\n-à env. 20 m de l\'arrêt des navettes<br/>\r\n-à env. 50 m des commerces, restaurants et bars<br/>\r\n-plusieurs chemins de randonnées hivernales dans les environs<br/><br/>\r\n\r\n<h2>Votre Chambre</h2>\r\n\r\n-hôtel en style de chalet avec 56 chambres équipées de wifi (gratuit), téléphone, TV, minicoffre, minibar, sèche-cheveux et WC.<br/><br/>\r\n\r\n<b>Type 2A:</b> chambre 2 personnes. Bain ou douche. Côté montagne.<br/><br/>\r\n\r\n<b>Type 2B:</b> chambre 2 personnes. Bain ou douche. Balcon. Vue sur la vallée.<br/><br/>\r\n\r\n<b>Type 2C:</b> chambre familiale 2/4 personnes Deluxe. Bain. Balcon. Vue sur la vallée.<br/><br/>\r\n\r\n<b>Type 2D:</b> chambre 2/3 personnes Deluxe, env. 35 m². Rénovée. Bain. Balcon. Vue sur la vallée.<br/><br/>\r\n\r\n<b>Type 1A:</b> chambre 1 personne. Bain ou douche. Côté montagne.<br/><br/>\r\n\r\n<b>Type 1B:</b> chambre 1 personne. Bain ou douche. Balcon. Vue sur la vallée.<br/><br/>\r\n\r\n\r\n<h2>Les Restaurants</h2>\r\n\r\n-restaurant avec vue panoramique<br/>\r\n-bar<br/><br/>\r\n\r\n<h2>Les Restaurants</h2>\r\n\r\n-petit-déjeuner buffet<br/>\r\n-dîner à 4 plats (demi-pension)<br/>\r\n-menu enfants jusqu\'à 9 ans inclus (un plat et dessert)<br/>\r\n-enfant de 10 à 12 ans inclus: menu à 3 plats<br/>\r\n-1x par semaine soirée valaisanne (demi-pension)<br/>\r\n-réveillons de Noël et du Nouvel An optionnels (à payer sur place)<br/><br/>\r\n\r\n<h2>Les Services</h2>\r\n-réception<br/>\r\n-wifi central (gratuit)<br/>\r\n-salon avec cheminée<br/>\r\n-local à skis<br/>\r\n-terrasse<br/>\r\n-2 ascenseurs<br/>\r\n-garage en dessous de l\'hôtel (gratuit)<br/><br/>\r\n\r\n<h2>Ambiance et Sport</h2>\r\n\r\n-piscine publique couverte (20x10 m)<br/>\r\n-sauna<br/>\r\n-bain de vapeur<br/>\r\n-bain bouillonnant en plein air<br/>\r\n-petit centre de bien-être<br/><br/>\r\n\r\n<h2>L\'Esprit de Famille</h2>\r\n-lit et chaise bébé', 'Dans le cadre d\'un séjour avec transport : le transport, l\'hébergement, les repas selon la formule choisie et les services comme mentionnés sur la présente offre. Dans le cadre d\'un séjour en rendez-vous sur place : l’hébergement, les repas selon la formule choisie et les services comme mentionnés sur la présente offre.', 'Dans le cadre d\'un séjour avec ou sans transport : les transferts aller/retour aéroport/lieu de séjour, l\'assurance voyage, les dépenses à caractère personnel, la taxe de séjour (sauf mention contraire).', '108', '150'),
(15, 'esp_img.jpg', 'esp_home.jpg', 'esp_golf.jpg', 'esp_resto.jpg', 'esp_room.jpg', '- Réservez tôt : jusqu\'à -300euros*<br/>\r\n- A proximité de la plage <br/>\r\n- Un sublime centre de bien-être', '<h2>Situation</h2>\r\n\r\nLe Club Coralia Ohtels Islantilla 4* se trouve sur la Costa de la Luz dans la province de Huelva, au sud-ouest de l’Espagne.<br/>\r\nIl bénéficie d’un emplacement idéal pour découvrir la région, à seulement 200 mètres de la plage de Islantilla et des commerces, 45 km de la ville de Huelva, et à environ 1h30 de l’aéroport de Séville.<br/><br/>\r\n\r\n<h2>Hébergement</h2>\r\n\r\nLe Club Coralia Ohtels Islantilla 4* compte au total 346 chambres réparties dans l’établissement de 4 à 6 étages.<br/><br/>\r\n\r\nDurant votre séjour, vous serez logés en chambre Standard : elles sont toutes équipées d’un balcon, d’un lit double ou de deux lits simples, d’un coin salon, climatisation, internet Wifi, télévision, coffre-fort (payant), réfrigérateur, téléphone, salle de bains complète avec sèche-cheveux.<br/><br/>\r\n\r\nL’occupation maximale est de : 3 adultes + 1 enfant, ou 2 adultes + 2 enfants.<br/><br/>\r\n\r\n<h2>Restaurants et bars</h2>\r\n\r\nVotre séjour au Club Coralia Ohtels Islantilla 4* se déroulera en formule tout compris comprenant tous les repas, snacks et boissons.<br/><br/>\r\n\r\nLe restaurant principal est ouvert pour les petits déjeuners (08h-10h30*), déjeuners (13h-15h*), et dîners (19h-22h*), et propose une cuisine internationale servie sous forme de buffets.<br/><br/>\r\n\r\nL’hôtel dispose également d’un snack et d’un bar proposant des boissons locales alcoolisées et non alcoolisées à volonté, ainsi que divers en-cas (de 10h à minuit).<br/><br/>\r\n\r\n* Les horaires sont communiqués à titre indicatif.<br/><br/>\r\n\r\n<h2>Sports et Loisirs</h2>\r\n\r\n<b>L’animation adulte en Club Coralia</b><br/>\r\nEn Club Coralia, nous avons la volonté de vous offrir une expérience club, plus axée sur les plaisirs simples des vacances. Notre équipe d’animateurs francophones vous proposera un programme d’animation en journée et en soirée, où les activités culturelles viendront compléter les « grands classiques » du club, tout en respectant le rythme de chacun.<br/>\r\n<u>- En journée :</u> en plus des activités sportives et ludiques qui font la richesse du programme d’animation des clubs Coralia nos animateurs vous feront découvrir des activités culturelles typiques : cours de cuisine locale et de cocktail, cours de danse locale, découverte de la langue locale,…<br/>\r\n<u>- En soirée :</u> nous vous proposerons des soirées rythmées et conviviales, encadrées par nos animateurs : soirée festive, sunset cocktail,  soirée Casino, soirée white, spectacle folklorique.<br/>\r\nCertaines activités peuvent-être annulées en fonction des conditions météorologiques. Enfin, sachez qu’au sein de l’hôtel, une équipe d’animation internationale vous proposera un programme d’activités variés, pour compléter le programme d’animation Coralia (sports collectifs, gym, spectacle,…).<br/><br/>\r\n                 \r\n<b>L’animation enfant en Club Coralia</b><br/>\r\n- Le Coralia Kids Club s’adresse aux enfants de 4 à 12 ans. Les animateurs qualifiés accueilleront vos enfants 6 jours sur 7, de 09h30 à 17h30 (accueil en continue) pour leur proposer un programme d’animation qui leur est spécialement dédié. Une fois par semaine et durant les vacances scolaires, vos enfants seront pris en charge dès le diner pour la soirée Kids Club. Les parents pourront ainsi profiter d’un moment privilégié.<br/>\r\n- Le Coralia Club Ado s’adresse aux adolescents de 13 à 17 ans uniquement pendant les vacances scolaires avec un programme à la carte, qui regroupera de nombreuses activités sportives, culturelles, et de rencontres.<br/><br/>\r\n\r\n<b>Activités de l’hôtel :</b><br/>\r\nL’établissement est situé à seulement 200 mètres de la belle plage de sable de Islantilla, rapidement accessible à pied.<br/>\r\nVous pourrez également vous détendre dans l’une des piscines de l’hôtel : une piscine extérieure aménagée de chaises longues et parasols ainsi que d’un bassin pour enfants, et une piscine intérieure (suivant la saison) avec bains à remous.\r\nUne salle de jeux est à disposition (foot sur table, billard, tennis de table).\r\nPour les plus sportifs, l’hôtel dispose d’une salle de sport, ainsi que d’un golf à proximité (en supplément).<br/><br/>\r\n\r\nN’hésitez pas à vous détendre au Spa de l’hôtel (payant), qui propose de nombreux soins et massages, ainsi que bain à remous, piscine intérieure, parcours aquatique, et bain turc.<br/><br/>\r\n\r\nD’autres services sont disponibles (dont certains en supplément) : Discothèque, blanchisserie, boutiques, salle de conférence<br/><br/>\r\n\r\n<h2>Notre Avis </h2>\r\n\r\nProfitez de vacances reposantes dans la charmante région de la Costa de la Luz au Club Coralia Ohtels Islantilla 4*, cet hôtel offre une ambiance familiale et des prestations de qualité au bord de la côte espagnole.', '- Les vols aller/retour<br/>\r\n- L\'hébergement en chambre standard pour la durée du séjour<br/>\r\n- Les transferts Aéroport/hôtel/aéroport<br/>\r\n- la pension de base<br/>\r\n- Les taxes aéroportuaires et surcharges', '- Les dépenses personnelles<br/>\r\n- Les assurances complémentaires facultatives<br/>\r\n- La taxe de séjour à payer sur place', '1050', '1085'),
(16, 'sant_img.jpg', 'sant_home.jpg', 'sant_desk.jpg', 'sant_resto.jpg', 'sant_room.jpg', 'Bel hôtel, idéalement situé près de Fira qui vous séduira par son architecture traditionnelle et colorée, et ses prestations de qualité.', '<h2>Situation</h2>\r\n\r\nA 450 m du centre de Fira, 8 km de l’aéroport, 14 km du port d’Athinios.<br/><br/>\r\n\r\n<h2>L\'hôtel</h2>\r\n\r\n207 logements (195 chambres standards et supérieures et 12 suites) dans de petites résidences.<br/><br/>\r\n\r\n<h2>Les chambres</h2>\r\n\r\nSalle de bains (baignoire ou douche/WC), sèche-cheveux, air conditionné, TV/satellite, réfrigérateur, minibar (sur demande, payant), accès Internet Wi-Fi (gratuit), coffre-fort, terrasse ou balcon (certains en commun) vue jardin ou piscine (selon disponibilités). Chambres individuelles standard uniquement. Pas de chambres communicantes. Lit bébé gratuit (compte comme 1 personne dans la capacité des chambres). <br/><br/>\r\n• Chambres standard : env. 18 m², jusqu’à 3 adultes ou 2 adultes + 1 enfant (canapé-lit pour 3e pers.). \r\n• Chambres supérieures : env. 25-28 m², pour 2 à 4 personnes (max. 3 adultes + 1 enfant). <br/><br/>\r\n• Suites : env. 28-30 m² : en duplex (pour 2 à 4 personnes, max. 2 adultes + 2 enfants, chambre et salon spacieux au 1er niveau avec 2 lits simples, 1 lit double en mezzanine) ou suite composée d’une chambre ouverte sur un grand salon jusqu’à 2 adultes et un enfant, 1 lit double ou 2 lits simples et un lit d’appoint pour l’enfant selon disponibilités).<br/><br/>\r\n\r\n<h2>Loisirs</h2>\r\n\r\n5 piscines d’eau douce avec chaises longues, parasols et serviettes, bassin séparé pour les enfants, 2 bains à remous extérieurs, salle de remise en forme (au centre Spa). <br/>Payant : centre Spa « Lotus » (à partir de 12 ans) avec piscine intérieure, massages, soins beauté, coiffeur.<br/><br/>\r\n\r\n<h2>Services</h2>\r\n\r\nRestaurant, bar principal, snack-bar. Coin Internet et Wi-Fi (gratuits) à la réception. Blanchisserie. Parking. Cartes de crédit acceptées.<br/><br/>\r\n\r\n<h2>Divers</h2>\r\n\r\nParties communes accessibles aux personnes à mobilité réduite (sauf piscine et plage), pas de chambre aménagées. Animaux non admis. Arrêt de bus à 450 m.', '• Le transport aérien par vol spécial pour Santorin <br/>\r\n• L\'accueil à l\'aéroport et les transferts <br/>\r\n• Le séjour (7 nuits) en chambre standard en petit déjeuner <br/>\r\n• Les taxes aéroportuaires et de solidarité, les frais de dossier (105 € à ce jour) <br/>\r\n• Notre assistance.', '• La taxe de séjour 3 € par chambre et par nuit (à régler en espèces sur place à l\'arrivée). <br/>\r\n• Les hausses carburants éventuelles des compagnies aériennes pouvant intervenir durant la saison <br/>\r\n• Les dépenses personnelles, les pourboires et extras.<br/> \r\n• Les assurances.', '1398', '1436'),
(17, 'prague_img.jpg', 'prague_home.jpg', 'prague_desk.jpg', 'prague_resto.jpg', 'prague_room.jpg', 'À l\'Hotel Praha, vous pouvez faire usage gratuitement du parking et du Wifi. Cet hôtel quatre étoiles se trouve à env. 400 m des remontées et à env. 150 m de l\'arrêt des navettes. Parking et Wifi sont offerts gratuitement par l\'hôtel.<br/><br/>\r\n\r\nHotel Praha dispose d\'une réception, d\'un restaurant, d\'un bar et d\'un ascenseur. Vous pouvez y faire usage gratuitement du parking et du wifi. L\'hôtel dispose également d\'un centre de bien-être où vous pouvez agréablement. Vous pouvez y profiter, entre autres, d\'une piscine couverte, d\'un sauna et d\'un jacuzzi. Moyennant paiement, vous pouvez aussi avoir un massage.', '<h2>BIENVENUE CHEZ VOUS</h2>\r\n\r\n-à 800 m d\'altitude<br/>\r\n-situé centralement, au-dessus du centre en vue de Spindleruv Mlyn<br/>\r\n-entouré des commerces et restaurants<br/><br/>\r\n\r\n<h2>Votre Chambre</h2>\r\n\r\nchambres confortables équipées wifi, TV-satellite, téléphone, minibar, douche et WC.<br/>\r\n\r\n<b>Type 2A:</b> chambre 2/3 personne<br/>\r\n\r\n<b>Type 2S:</b> chambre 2 personne promotionelle<br/>\r\n\r\n<b>Type 3A: </b>chambre familiale 3/5 personnes. 2 chambres contiguës avec communicantes.<br/><br/>\r\n\r\n\r\n\r\n<h2>Les Restaurants</h2>\r\n\r\n-restaurant<br/>\r\n-bar<br/>\r\n-discothèque<br/><br/>\r\n\r\n<h2>Les Restaurants</h2>\r\n\r\n-petit-déjeuner buffet<br/>\r\n-dîner à 3 plats (demi-pension)<br/>\r\n-réveillons de Noël optionnels (dîner à 5 plats, payer sur place env. € 48 par personnes ; enfants 7 à 14 ans inclus env. € 24 par personnes et enfants 2 à 6 ans inclus gratuit)<br/><br/>\r\n\r\n<h2>Les Services</h2>\r\n\r\n-réception<br/>\r\n-lounge<br/>\r\n-wifi (gratuit)<br/>\r\n-ascenseur<br/>\r\n-parking (gratuit)<br/><br/>\r\n\r\n<h2>Ambiance et Sport</h2>\r\n\r\n-massage\r\n\r\n<h2>L\'Esprit de Famille</h2>\r\n\r\n-lit bébé', 'Dans le cadre d\'un séjour avec transport : le transport, l\'hébergement, les repas selon la formule choisie et les services comme mentionnés sur la présente offre. Dans le cadre d\'un séjour en rendez-vous sur place : l’hébergement, les repas selon la formule choisie et les services comme mentionnés sur la présente offre.', 'Dans le cadre d\'un séjour avec ou sans transport : les transferts aller/retour aéroport/lieu de séjour, l\'assurance voyage, les dépenses à caractère personnel, la taxe de séjour (sauf mention contraire).', '118', '142'),
(18, 'lond_desk.jpg', 'lond_home.jpg', 'lond_rooom.jpg', 'lond_resto.jpg', 'lond_room.jpg', 'The Queens Park à Londres est un hôtel trois étoiles soigné avec des chambres confortables. L\'hôtel se situe à env. 5 minutes de marche du centre commercial Whiteley\'s et de Queensway animé, tout près de Hyde Park.<br/><br/>\r\n\r\nL\'hôtel met à votre disposition une réception, un salon, deux ascenseurs et le wifi (payant). Les 100 chambres sont équipées de wifi (payant), téléphone, TV-satellite, minicoffre, possibilité de préparer du thé/café, presse-pantalon, sèche-cheveux, douche et WC. Des chambres non-fumeurs sont également à votre disposition.<br/><br/>\r\n\r\nLe matin, un petit-déjeuner buffet copieux et un petit-déjeuner anglais vous sont proposés dans la salle de petit-déjeuner de l\'hôtel.', '<h2>BIENVENUE CHEZ VOUS</h2>\r\n\r\n-Bayswater<br/>\r\n-à env. 5 minutes de marche du centre commercial Whiteley’s et de Queensway<br/>\r\n-parking dans les environs: env. 25 GBP (env. € 27,70) par jour<br/>\r\n-métro: Queensway (Central Line) et Bayswater (Circle, directe depuis Saint Pancras International et District Lines), les deux à env. 200 m<br/><br/>\r\n\r\n\r\n</h2>Votre Chambre</h2>\r\n\r\n100 chambres (7 étages) avec wifi (payant), téléphone, TV-satellite, minicoffre, possibilité de préparer du thé/café, presse-pantalon, sèche-cheveux, douche et WC. Possibilité de chambres non-fumeurs.<br/><br/>\r\n\r\n\r\n<b>Type 2A: </b>chambre 2/3 personnes. 3e personne de moins de 13 ans.<br/><br/>\r\n\r\n<b>Type 3A:</b> chambre 3 personnes.<br/><br/>\r\n\r\n<b>Type 1A:</b> chambre 1 personne.<br/><br/>\r\n\r\n\r\n<h2>Les Restaurants</h2>\r\n\r\n-petit-déjeuner buffet copieux<br/>\r\n-petit-déjeuner anglais<br/><br/>\r\n\r\n<h2>Les Services</h2>\r\n\r\n-réception<br/>\r\n-salon<br/>\r\n-2 ascenseurs<br/>\r\n-wifi central<br/><br/>\r\n\r\n<h2>L\'Esprit de Famille</h2>\r\n\r\n-lit bébé: pas possible', 'Dans le cadre d\'un séjour avec transport : le transport, l\'hébergement, les repas selon la formule choisie et les services comme mentionnés sur la présente offre. Dans le cadre d\'un séjour en rendez-vous sur place : l’hébergement, les repas selon la formule choisie et les services comme mentionnés sur la présente offre.', 'Dans le cadre d\'un séjour avec ou sans transport : les transferts aller/retour aéroport/lieu de séjour, l\'assurance voyage, les dépenses à caractère personnel, la taxe de séjour (sauf mention contraire).', '138', '192'),
(19, 'andorre_img.jpg', 'andorre_home.jpg', 'andorre_pool.jpg', 'andorre_resto.jpg', 'andorre_room.jpg', 'Situé à 100 m du centre du village d\'Arinsal, l\'hôtel Princesa Parc et tout proche de départ de sentiers de randonnées.<br/>\r\nLa capitale, Andorre-La-Vieille est à environ 10 km, et la frontière française au Pas de la Case à environ 30 km.', '<h2>Présentation</h2>\r\n\r\nSitué à 100 m du centre du village d\'Arinsal, l\'hôtel Princesa Parc et tout proche de départ de sentiers de randonnées.<br/>\r\nLa capitale, Andorre-La-Vieille est à environ 10 km, et la frontière française au Pas de la Case à environ 30 km.<br/><br/>\r\n\r\n<h2>Restauration</h2>\r\n\r\nDurant votre séjour vous serez hébergés en formule tout inclus (détails voir rubrique concernée)<br/><br/>\r\n\r\n<h2>Les plus</h2>\r\n\r\nFormule tout compris<br/>\r\nCentre remise en forme complet<br/>\r\nSituation idéale proche village et sentiers<br/><br/>\r\n\r\n<h2>Sports et loisirs</h2>\r\n\r\n- Piscine extérieure chauffée (15/6-16/9).<br/>\r\n- Accès au centre Wellness (sandales obligatoires) : piscine hydro-massages, bains à remous, saunas, hammam. Ouvert 7 j/7 du 19/6 au 10/9, jours confirmés sur place pour autres périodes. Le centre Wellness sera fermé du 9/4 au 13/4/18 pour cause de maintenance.<br/>\r\n- 4 pistes de bowling, billard. Salle de fitness. <br/>\r\n- Randonnées accompagnées (15/6-16/9), 6 j/7.<br/>\r\n- Cocktail bar, piano-bar, bar au bowling (ouverts selon période).', '- Le séjour selon le type d\'hébergement choisi et en demi-pension.<br/>\r\n- Les services, loisirs et activités mentionnés sans supplément.<br/>\r\n- La taxe de séjour.', '- Le transport aller/retour. <br/>\r\n- Les frais de dossier,<br/>\r\n- Les repas et les boissons non inclus dans la formule. <br/>\r\n- Les dépenses d\'ordre personnel.<br/>\r\n- Les activités payantes. <br/>\r\n- Les garanties assistance, rapatriement, frais médicaux et d\'hospitalisation, assistance juridique et pénale.<br/>\r\n- Les assurances annulation, bagages, responsabilité civile.', '282', '323'),
(20, 'athe_img.jpg', 'athe_home.jpg', 'athe_desk.jpg', 'athe_resto.jpg', 'athe_room.jpg', 'Situé dans la mer Egée, l\'archipel des Cyclades est composé d\'environ 2200 îles, îlots et îlots-rochers qui forment un cercle autour de l\'île de Délos. L\'archipel regroupe 24 îles habitées et offre des paysages diversifiés : des plages de sable fin, des criques, des montagnes, des vallées verdoyantes ou rocailleuses, des falaises, des petits villages perchés, des maisons à la blancheur éclatante et des chapelles au bleu infini de la mer Égée. L\'archipel des Cyclades réunit notamment les îles de Santorin, Mykonos, Délos, Paros et Naxos.', '<h2>Présentation</h2>\r\n\r\nSitué dans la mer Egée, l\'archipel des Cyclades est composé d\'environ 2200 îles, îlots et îlots-rochers qui forment un cercle autour de l\'île de Délos. L\'archipel regroupe 24 îles habitées et offre des paysages diversifiés : des plages de sable fin, des criques, des montagnes, des vallées verdoyantes ou rocailleuses, des falaises, des petits villages perchés, des maisons à la blancheur éclatante et des chapelles au bleu infini de la mer Égée. L\'archipel des Cyclades réunit notamment les îles de Santorin, Mykonos, Délos, Paros et Naxos.<br/>\r\nUn paradis authentique avec une douceur de vivre. Entre sites archéologiques et traditions gastronomiques, vous serez charmés par la variété des paysages des Cyclades.<br/><br/>\r\n\r\n\r\nCe boutique-hôtel propose des prestations de qualité et offre une superbe vue sur la mer depuis les chambres ! Sa situation est idéale pour apprécier l\'ambiance et les soirées animées de l\'île !<br/><br/>\r\n\r\n<h2>Restauration</h2>\r\n\r\nDurant votre séjour, vous serez logés en formule petit-déjeuner :<br/>\r\n- Petit déjeuner continental (buffet).<br/><br/>\r\n\r\nAvec supplément : <br/>\r\n- Restaurant italien à la carte (avec chef étoilé Michelin).<br/>\r\n- Bar avec terrasse vue mer.<br/><br/>\r\n\r\n<h2>Sports et loisirs</h2>\r\n\r\n- Piscine aménagée en front de mer.<br/>\r\n- Face à une plage de sable avec chaises longues et parasols.', '- L\'accueil au comptoir FRAM à l\'aéroport de départ.<br/>\r\n- Le vol aller et retour.<br/>\r\n- Le forfait taxes et redevances des aéroports.<br/>\r\n- L\'accueil à l\'arrivée.<br/>\r\n- Le transfert aller et retour de l\'aéroport à l\'hôtel.<br/>\r\n- Le séjour selon le type d\'hébergement et en petit-déjeuner.<br/>\r\n- Les services, loisirs et activités mentionnés sans supplément.\r\n', '- Les frais de dossier,<br/>\r\n- Les taxes éventuelles de séjour et de sortie du territoire.<br/>\r\n- Les repas et les boissons non inclus dans la formule.<br/>\r\n- Les dépenses d\'ordre personnel<br/>\r\n- Les excursions facultatives, et les activités non mentionnées au programme.<br/>\r\n- Les repas éventuels aux escales.<br/>\r\n- Les garanties assistance, rapatriement, frais médicaux et d\'hospitalisation, <br/>assistance juridique et pénale.<br/>\r\n- Les assurances annulation, bagages, responsabilité civile.', '906', '971'),
(21, 'cuba_IMG.jpg', 'cuba_POOL.jpg', 'cuba_home.jpg', 'cuba_resto.jpg', 'cuba_room.jpg', 'Situé au coeur du quartier du Vedado, à 200 m du Malecon et à 10 mn du centre historique de la Vieille ville de La Havane, cet hôtel des années 20 n’a rien perdu de son charme. Plus de 400 pièces d’art cataloguées y sont exposées, ce qui procure au visiteur dès son arrivée une sensation très spéciale.', '<h2>BIENVENUE CHEZ VOUS</h2>\r\n\r\n<h2>Les chambres</h2>\r\n\r\nLes 158 chambres, réparties sur 10 étages, sont équipées d’une salle de bains avec sèche-cheveux, climatisation, TV satellite, téléphone, mini réfrigérateur, coffre (payant). <br/><br/>\r\n\r\n<b>Type A : </b>\r\nChambre double ou à usage individuel standard <br/>\r\nPossibilité de chambres communicantes<br/>\r\nLits bébé inclus dans la capacité totale de la chambre<br/><br/>\r\n\r\n<b>Type B : </b>\r\nSuites, plus spacieuses<br/><br/>\r\n\r\n<h3>Au menu</h3>\r\nLe petit déjeuner. La demi-pension en option. 3 restaurants, 2 bars, 1 lobby bar terrasse et 1 snack bar.<br/><br/>\r\n\r\n<h3>Les services</h3>\r\nChaises longues. Avec participation : blanchisserie, service médical, connexion Wi-fi, location de voiture.<br/><br/>\r\n\r\n<h2>AMBIANCE ET SPORT</h2>\r\n\r\n<h3>Les sports</h3>\r\nUne piscine.', '\r\n-Le transport aérien aller et retour sur vol spécial ou régulier<br/>\r\n-Les transferts aéroport - hôtel - aéroport<br/>\r\n-Le logement selon chambre sélectionnée avec la pension selon la formule choisie. Par exemple, pour les séjours de 8 jours/7 nuits, la demi-pension comprend 7 petits déjeuners et 7 déjeuners ou dîners selon descriptif de l\'hôtel choisi, la pension complète et la formule \" Tout compris \" comprennent 7 petits déjeuners, 7 déjeuners et 7 dîners. Toutefois selon les horaires d\'arrivée et de départ, le premier et/ou le dernier repas pourront ne pas être servis.<br/>\r\n-L\'accueil et l\'assistance sur place, l\'accès aux services et infrastructures de l\'hôtel (sauf prestations facultatives en supplément)<br/>\r\n-Les frais de dossiers, les taxes d\'aéroport et les taxes hôtelières, les taxes de sûreté et redevances passagers.', '-Les assurances annulation, rapatriement et bagages.<br/>\r\n-Les dépenses personnelles, les pourboires.<br/>\r\n-Les taxes de séjour ou de sortie du territoire à régler sur place, les frais liés aux formalités (visas, vaccinations, passeport).<br/>\r\n-Les frais de dossier de l’agence.', '1986', '2031');
INSERT INTO `trip` (`idTrip`, `img`, `home_img`, `desk_img`, `resto_img`, `room_img`, `essential`, `detail`, `price_have`, `price_dont`, `priceRoom`, `priceRoomSup`) VALUES
(22, 'flo_img.jpg', 'flo_home.jpg', 'flo_desk.jpg', 'flo_resto.jpg', 'flo_room.jpg', 'Le Chianti Village bénéficie d’une situation exceptionnelle, au cœur des coteaux du Chianti. C’est un excellent point de chute pour visiter les villes de Florence, Sienne et San Gimignano par exemple.<br/><br/>\r\n\r\nLe complexe dispose de différentes infrastructures comme une réception, un salon, le wifi central et un parking gratuit. Après vous être baladé dans la région, rafraîchissez-vous dans la piscine en plein air.<br/><br/>\r\n\r\nOutre le petit-déjeuner, le Chianti Restaurant Morocco sert aussi de délicieuses spécialités régionales.', '<h2>BIENVENUE CHEZ VOUS</h2>\r\n\r\n-dans le quartier de Chianti Classico<br/>\r\n-à env. 4 km de centre du Tavarnelle<br/>\r\n-à env. 30 km de Florence<br/>\r\n-à env. 35 km de Sienne<br/><br/>\r\n\r\n<h2>Votre Chambre</h2>\r\n56 appartements, équipés de wifi (gratuit), téléphone, TV-satellite et minicoffre. Coin salon, coin repas et kitchenette avec plaques chauffantes, micro-ondes, réfrigérateur et lave-vaiselle. Climatisation. Salle de bains avec sèche-cheveux, douche et WC. <br/>Balcon ou terrasse avec mobilier de jardin.<br/><br/>\r\n\r\n<b>Type 2F:</b> studio 2 personnes: env. 30 m². Chambre/séjour avec lit double.<br/><br/>\r\n\r\n<b>Type 2G/3G/4G:</b> appartement 2 pièces 2/3/4 personnes: env. 40 m². Chambre/séjour avec banquette-lit double et chambre 2 personnes.<br/><br/>\r\n\r\n<b>Type 3H/4H/5H:</b> appartement 3 pièces 3/4/5 personnes: env. 52 m². Chambre/séjour avec banquette-lit double, chambre 2 personnes et chambre 1 personne.<br/><br/>\r\n\r\n<h2>Les Restaurants</h2>\r\n\r\n-salle de petit-déjeuner/restaurant avec terrasse<br/>\r\n-bar avec salon TV<br/><br/>\r\n\r\n<h2>Les Restaurants</h2>\r\n\r\n-petit-déjeuner buffet (logement petit-déjeuner)<br/><br/>\r\n\r\n<h2>Les Services</h2>\r\n\r\n-réception<br/>\r\n-wifi central (gratuit)<br/>\r\n-salon<br/>\r\n-jardin<br/>\r\n-parking (gratuit)<br/><br/>\r\n\r\n<h2>Ambiance et Sport</h2>\r\n\r\n-piscine en plein air (16x8 m; ouverte: 15/4-15/10; selon le météo) avec chaises longues et parasols<br/>\r\n-service de linge de bain à la piscine<br/><br/>\r\n\r\n<b>dans les environs:</b>\r\n-tennis (env. 3 km)<br/>\r\n-équitation (env. 10 km)<br/>\r\n-golf (env. 25 km)<br/><br/>\r\n\r\n<h2>L\'Esprit de Famille</h2>\r\n-piscine intégrée pour enfants <br/>\r\n-lit bébé (à signaler lors de la réservation)', '-consommation d\'eau et électricité<br/>\r\n-chauffage<br/>\r\n-climatisation<br/>\r\n-linge de lit et de bain (changement hebdomadaire)<br/>\r\n-nettoyage final (sauf kitchenette et vaisselle; si la kitchenette est insuffisamment -nettoyée, € 35 seront déduits de la caution )', 'Dans le cadre d\'un séjour avec ou sans transport :Les transferts aller/retour aéroport/lieu de séjour, l\'assurance voyage, les dépenses à caractère personnel, la taxe de séjour (sauf mention contraire).', '141', '166'),
(23, 'dub_img.jpg', 'dub_home.jpg', 'dub_desk.jpg', 'dub_resto.jpg', 'dub_room.jpg', '- Un emplacement idéal en centre-ville<br/>\r\n- Un toit-terrasse avec piscine<br/>\r\n- Un personnel à l\'écoute', '<h2>Situation</h2>\r\n\r\nSitué en centre-ville dans le quartier historique de Bur Dubaï, le Citymax Hotel Bur est à proximité de la station de métro Khalid Bin Walid, des boutiques Karama et des restaurants. <br/>\r\nLe célèbre et plus grand centre commercial du monde, le « Dubai Mall », se situe à 20min environ en voiture.<br/>\r\nLe Centre commercial « BurJuman » est situé à 20min à pied environ et Le Dubai World Trade Centre est à 3km environ.<br/>\r\nL’aéroport international de Dubai est à seulement 20 minutes en voiture.<br/><br/>\r\n\r\n<h2>Hebergement</h2>\r\n\r\nL’hôtel Citymax Bur Dubaï dispose de 691 chambres joliment décorées.<br/>\r\nToutes les chambres sont équipées de matelas de luxe, de la climatisation à réglage individuel, d\'un écran TV LCD 32 \'\', d’une salle de bain avec une baignoire ou une douche à jets, d’un coffre-fort, thé et café à disposition, d’un réfrigérateur, d’un téléphone et un accès Wif-Fi gratuit.<br/><br/>\r\n\r\n<h2>Restaurants et bars</h2>\r\n\r\nAfin de satisfaire toutes vos envies, l’hôtel vous propose différents point de restauration très variés.<br/>\r\n- <b>Le \"City Café - All Day Dining\" :</b> vous propose une cuisine internationale, continentale, asiatique et arabe, et d’un menu à la carte. Ce restaurant est ouvert toute la journée.<br/>\r\n- <b>Le \"Claypot\" :</b> vous propose une cuisine Indienne et vous fera revivre le riche patrimoine culinaire de l’Inde.<br/>\r\n- <b>Le \"Sizzling Wok\" :</b> vous propose une cuisine asiatique<br/>\r\n- <b>Le \"The Huddle Sports Bar & Grill\" :</b> vous propose des grillades et une cuisine internationale. De plus, profiter de vos maths préférés (Football, Criket, Rugby…) en direct, avec écrans géants, ainsi que d’une table de billard et un Live Band.<br/>\r\n- <b>Le \"The 24-Hour Coffee Shop\" :</b> ce café est ouvert toute la journée et vous propose des pâtisseries.<br/><br/>\r\n\r\n<h2>Equipements et activités</h2>\r\n\r\nCet hôtel dispose d’une piscine extérieure sur le toit, un bar/piscine, d’un centre de remise en forme, d’une réception 24h/24, d\'ascenseurs, des chaises longues (selon disponibilité) à la piscine, des parasols (selon disponibilité) à la piscine,  des serviettes de bain à la piscine et un parking privé gratuit.<br/><br/>\r\n\r\n<h2>Notre Avis</h2>\r\n\r\nIdéalement bien placé au cœur de la ville dans un quartier en pleine évolution, le Citymax Hôtel Bur Dubai 3*est un excellent choix pour découvrir Dubaï et ses environs.', '- les vols aller et retour <br/>\r\n- l’accueil et l’assistance<br/> \r\n- les taxes aéroportuaires et surcharges carburant (soumis à variation)<br/>\r\n- les transferts aller et retour aéroport/hôtel<br/>\r\n- l’hébergement en chambre double pour la durée du séjour<br/>\r\n- la pension selon programme    ', '- les frais de dossier<br/>\r\n- les assurances complémentaires facultatives<br/>\r\n- les prestations payantes (activités, excursions…)<br/>\r\n- les dépenses personnelles<br/>\r\n- les pourboires usuels<br/>\r\n- les éventuelles taxes de sortie du territoire et visa d’entrée<br/>\r\n- les éventuelles taxes de séjours (à régler sur place à l’hôtel) et frais de ménage<br/>\r\n- les repas non mentionnés <br/>\r\n- les éventuels services / resort Fees', '1404', '1472'),
(24, 'gua_img.jpg', 'gua_home.jpg', 'gua_desk.jpg', 'gua_resto.jpg', 'gua_room.jpg', 'Bien caché dans un quartier résidentiel, le Marifa est un petit hôtel de charme avec piscine proposant des studios et des duplex. Très calme et paisible, il séduira tout particulièrement les amateurs de nature et de tranquillité de même que les vacanciers désirant découvrir la Guadeloupe hors des sentiers battus. ', '<h2>En résumé</h2>\r\n\r\nBien caché dans un quartier résidentiel, le Marifa est un petit hôtel de charme avec piscine proposant des studios et des duplex. Très calme et paisible, il séduira tout particulièrement les amateurs de nature et de tranquillité de même que les vacanciers désirant découvrir la Guadeloupe hors des sentiers battus. La plage n\'est quant à elle qu\'à quelques minutes en voiture. Dans les jardins, les étonnants arbres du voyageur ajoutent à cette impression de dépaysement.<br/><br/>\r\n\r\n<h2>L\'espace privé</h2>\r\n\r\nL\'hôtel Marifa est un hôtel de charme en Guadeloupe, situé entre Gosier et Sainte Anne, il se trouve dans un cadre verdoyant, à quelques minutes de la mer .<br/>\r\nLa résidence est constitée de 4 bâtiments de trois niveaux.<br/><br/>\r\n\r\n<h2>Studio</h2>\r\n\r\n28 Studios d\'environ 40m².<br/>\r\nIls peuvent accueillir 3 adultes ou 2 adultes et 1 enfant.<br/>\r\n1 Studio est aménagé pour les personnes à mobilité réduite, il peut accueillir 2 personnes.<br/>\r\nSitués en rez-de-chaussée ou en étage, ils disposent d\'une kitchenette et d\'une loggia.<br/>\r\nIls sont équipés de : Climatisation - Salle de bains - Sèche-cheveux - Téléphone - Télévision - Coffre-fort individuel.<br/><br/>\r\n\r\n<h2>L\'espace restauration</h2>\r\n\r\nPetits déjeuners servis le matin de 07h00 à 09h30.<br/><br/>\r\n\r\n<h2>L\'Espace Sport, Loisirs et Détente</h2>\r\n\r\nLa résidence dispose d\'une piscine et domine la plage de Petit Havre que l\'on peut rejoindre à pieds en quelques minutes.', '-Les vols réguliers Aller/Retour<br/>\r\n-L\'accueil et l\'assistance par notre représentant local<br/>\r\n-Les transferts Aéroport/Hôtel/Aéroport sauf si prise d\'une location de voiture<br/>\r\n-Les nuits d\'hôtel<br/>\r\n-Les petits déjeuner', '-Les assurances facultatives<br/>\r\n-Les dépenses personnelles et les pourboires<br/>\r\n-Les repas et boissons non mentionnés<br/>\r\n-Les taxes locales de séjour<br/>\r\n-Les navettes inter-aéroport', '1390', '1427'),
(25, 'dan_img.jpg', 'dan_home.jpg', 'dan_rooom.jpg', 'dan_resto.jpg', 'dan_room.jpg', 'Comfort Hotel Vesterbro est un hôtel quatre étoiles agréable situé dans le quartier à la mode de Vesterbro. Le parc d\'attractions bien connu Tivoli se trouve à courte distance à pied.<br/><br/>\r\n\r\nL\'hôtel est aménagé de façon moderne et dispose d\'une réception, d\'un salon, d\'un bar et d\'un restaurant. Le matin, vous pouvez jouir d\'un petit-déjeuner buffet soigné au restaurant, et le soir vous y pouvez manger un morceau aussi. À la suite d\'un long jour, rempli de visites à les nombreux choses à voir à Copenhague, vous pouvez toujours aller au salon et au bar pour prendre un petit noir ou un verre rafraîchissant.<br/><br/>\r\n\r\nLes 401 chambres sont équipées de, entre autre, wifi gratuit, téléphone, TV, réfrigérateur, climatisation, bain ou douche et WC.', '<h2>BIENVENUE CHEZ VOUS</h2>\r\n\r\n-à env. 500 m du parc d\'attractions renommé Tivoli<br/>\r\n-à env. 750 m de la Rådhuspladsen<br/>\r\n-à env. 300 m de la gare centrale (København H)<br/>\r\n-bus: Vesterbros Torv à env. 200 m<br/><br/>\r\n\r\n<h2>Votre Chambre</h2>\r\n\r\n401 chambres équipées de wifi (gratuit), téléphone, TV, réfrigérateur, climatisation, bain ou douche et WC.<br/><br/>\r\n\r\n<b>Type 2A:</b> chambre 2 personnes.<br/><br/>\r\n\r\n<b>Type 1A:</b> chambre 1 personne.<br/><br/>\r\n\r\n\r\n<h2> Restaurants</h2>\r\n\r\n-restaurant<br/>\r\n-bar<br/>\r\n-petit-déjeuner buffet<br/><br/>\r\n\r\n<h2>Les Services</h2>\r\n\r\n-réception<br/>\r\n-wifi central (gratuit)<br/>\r\n-salon<br/>\r\n-ascenseur<br/>', 'Dans le cadre d\'un séjour avec transport : le transport, l\'hébergement, les repas selon la formule choisie et les services comme mentionnés sur la présente offre. Dans le cadre d\'un séjour en rendez-vous sur place : l’hébergement, les repas selon la formule choisie et les services comme mentionnés sur la présente offre.', 'Dans le cadre d\'un séjour avec ou sans transport : les transferts aller/retour aéroport/lieu de séjour, l\'assurance voyage, les dépenses à caractère personnel, la taxe de séjour (sauf mention contraire).', '300', '336'),
(26, 'ams_img.jpg', 'ams_home.jpg', 'ams_rooom.jpg', 'ams_resto.jpg', 'ams_room.jpg', 'Best Western Premier Couture est un hôtel 4 étoiles confortable situé dans le quartier de Slotervaart. La Leidseplein et la Museumplein où se trouvent le Rijksmuseum et le Musée Van Gogh sont situées à env. 15 minutes en tram.<br/><br/>\r\n\r\nLe petit-déjeuner sous forme de buffet est compris dans le séjour. Détendez-vous et profitez du confort de votre chambre grâce au WiFi gratuit et à la télévision avec ses chaînes cinéma et sport, ou préparez-vous une tasse de café avec l’appareil Nespresso®.<br/><br/>\r\n\r\nLouez un vélo à l’hôtel et faufilez-vous à travers les rues animées du centre-ville comme un véritable Amstellodamois. Une manière agréable de découvrir la ville !\r\n', '<h2>BIENVENUE CHEZ VOUS</h2>\r\n\r\n-sur du périphérique, a côté du World Fashion Center<br/>\r\n-tramway: Delflandlaan devant l\'hôtel, dans env. 15 minutes, vous êtes à la Leidseplein ou Museumplein<br/>\r\n-garage: env. € 15 par 24h<br/>\r\n-15 min en voiture de l\'aéroport de Schiphol<br/><br/>\r\n\r\n<h2>Votre Chambre</h2>\r\n56 appartements, équipés de wifi (gratuit), téléphone, TV-satellite et minicoffre. Coin salon, coin repas et kitchenette avec plaques chauffantes, micro-ondes, réfrigérateur et lave-vaiselle. Climatisation. Salle de bains avec sèche-cheveux, douche et WC. <br/>Balcon ou terrasse avec mobilier de jardin.<br/><br/>\r\n\r\n<b>Type 2F:</b> studio 2 personnes: env. 30 m². Chambre/séjour avec lit double.<br/><br/>\r\n\r\n<b>Type 2G/3G/4G:</b> appartement 2 pièces 2/3/4 personnes: env. 40 m². Chambre/séjour avec banquette-lit double et chambre 2 personnes.<br/><br/>\r\n\r\n<b>Type 3H/4H/5H:</b> appartement 3 pièces 3/4/5 personnes: env. 52 m². Chambre/séjour avec banquette-lit double, chambre 2 personnes et chambre 1 personne.<br/><br/>\r\n\r\n<h2>Les Restaurants</h2>\r\n\r\n-salle de petit-déjeuner/restaurant avec terrasse<br/>\r\n-bar avec salon TV<br/><br/>\r\n-petit-déjeuner buffet (logement petit-déjeuner)<br/><br/>\r\n\r\n<h2>Les Services</h2>\r\n\r\n-réception<br/>\r\n-wifi central (gratuit)<br/>\r\n-salon<br/>\r\n-jardin<br/>\r\n-parking (gratuit)<br/><br/>\r\n\r\n<h2>Ambiance et Sport</h2>\r\n\r\n-piscine en plein air (16x8 m; ouverte: 15/4-15/10; selon le météo) avec chaises longues et parasols<br/>\r\n-service de linge de bain à la piscine<br/><br/>\r\n\r\n<b>dans les environs:</b>\r\n-tennis (env. 3 km)<br/>\r\n-équitation (env. 10 km)<br/>\r\n-golf (env. 25 km)<br/><br/>\r\n\r\n<h2>L\'Esprit de Famille</h2>\r\n-piscine intégrée pour enfants <br/>\r\n-lit bébé (à signaler lors de la réservation)', 'Dans le cadre d\'un séjour avec transport : le transport, l\'hébergement, les repas selon la formule choisie et les services comme mentionnés sur la présente offre. Dans le cadre d\'un séjour en rendez-vous sur place : l’hébergement, les repas selon la formule choisie et les services comme mentionnés sur la présente offre.', 'Dans le cadre d\'un séjour avec ou sans transport : les transferts aller/retour aéroport/lieu de séjour, l\'assurance voyage, les dépenses à caractère personnel, la taxe de séjour (sauf mention contraire).', '358', '378'),
(27, 'ken_img.jpg', 'ken_home.jpg', 'ken_rooom.jpg', 'ken_resto.jpg', 'ken_room.jpg', ' - directement sur la plage <br/>\r\n - détails de plage: Tiwi Beach; sable fin, très longue plage <br/>\r\n - distance (environ) : de la localité Mombasa: 32 km, du terrain de golf: 15 km', '<h2>Situation</h2>\r\n\r\n - directement sur la plage<br/>\r\n - détails de plage: Tiwi Beach; sable fin, très longue plage<br/>\r\n - distance (environ) : de la localité Mombasa: 32 km, du terrain de golf: 15 km<br/><br/>\r\n\r\n<h2>Equipement</h2>\r\n\r\n - nature du complexe hôtelier: complètement rénové <br/>\r\n - nombre total de chambres/logements: 209<br/>\r\n - hall d\'accueil, réception <br/>\r\n - nombre total de restaurants: 4<br/>\r\n - 1 restaurant buffet ; 3 restaurant à la carte <br/>\r\n - nombre de bars: 5<br/>\r\n - boutique, coiffeur <br/>\r\n - équipements extérieurs: tropical; équipement : jardin, terrasse <br/>\r\n - piscine-nombre total: 1<br/>\r\n - espace baignade (eau salée)<br/>\r\n - chaises longues (selon disponibillité) : à la piscine (inclus); à la plage (inclus)<br/>\r\n - serviettes de plage: à la piscine (inclus); à la plage (inclus)<br/>\r\n - cartes de crédit : VISA, Mastercard, Amexco<br/><br/>\r\n\r\n\r\n<h2>chambre double côté piscine</h2>\r\n\r\n - agréable<br/>\r\n - salle(s) de bains/WC<br/>\r\n - mini réfrigérateur, moustiquaire, nécessaire à café/thé, sèche-cheveux, téléphone, télévision, balcon ou terrasse<br/>\r\n - WIFI (inclus)<br/>\r\n - coffre-fort (inclus)<br/>\r\n - climatisation<br/>\r\n - possibilité à usage individuel<br/><br/>\r\n\r\n<h2>chambre double de luxe</h2>\r\n\r\n - agréable<br/>\r\n - salle(s) de bains ou douche/WC<br/>\r\n - chaussons, fer à repasser, mini réfrigérateur, moustiquaire, nécessaire à café/thé, peignoir(s), sèche-cheveux, téléphone, télévision, balcon ou terrasse<br/>\r\n - WIFI (inclus)<br/>\r\n - coffre-fort (inclus)<br/>\r\n - climatisation<br/><br/>\r\n\r\n<h2>Restauration</h2>\r\n\r\n - demi pension, y compris petit-déjeuner (buffet) et dîner (buffet)<br/>\r\n - pension complète, y compris petit-déjeuner (buffet), déjeuner (buffet) et dîner (buffet) <br/>\r\n - Tout Compris<br/>\r\n - végétarien', ' - Le vol aller / retour<br/>\r\n - Les taxes d\'aéroport.<br/>\r\n - Les transferts aéroport/hôtel/aéroport.<br/>\r\n - Le séjour de la durée choisie, dans l\'établissement retenu.<br/>\r\n - Le type d\'hébergement et la formule de restauration sélectionnés.<br/>\r\n - L\'assistance sur place.<br/>\r\n - Des documents de voyage.', '- Les pourboires et dépenses personnelles<br/>\r\n - Les frais liés aux formalités d\'entrée dans le pays<br/>\r\n - Les repas et boissons non mentionnés<br/>\r\n - Les services et activités payants<br/>\r\n - Les éventuels suppléments à bord de l\'avion (collations et boissons sur certaines compagnies)<br/>\r\n - Les assurances facultatives<br/>\r\n - Les éléments non mentionnés dans « notre offre comprend »', '1674', '1725');
